//     payback: null,
//   }

// export const removeFarmState = atom({
//   key: 'removeFarmState',
//   default: emptyFarmState
// })

export const Remove: React.FC = () => {
  const { positionId, collateralSize, name, wrapper, spell, lp, tokens } = useParams<{ positionId: string, collateralSize: string, name: string, wrapper: string, spell: string, lp:string, tokens:string}>();
  const setPool = useSetRecoilState(poolState); 
  const setPosition = useSetRecoilState(removePositionState); 

  const set: poolProps  = {
      name: name,
      wrapper: wrapper,
      spell: spell,
      lp: lp,
      tokens: (tokens.split(',').map((x) => getToken(x)!)),
  }
  setPool(set); 
  setPosition({
    positionId, 
    collateralSize,
  });

  const [page] = useRecoilState(removePageState);

  return (
    <div>
      {
        <Remove />
