 *
 * Total Supply: homoraBank.banks(token).ctoken.totalSupply()
 * Total Borrow: homoraBank.banks(token).ctoken.totalBorrows()
 * Utilization: jumpRateModel.utilizationRate(Total Supply, Total Borrow, homoraBank.banks(token).ctoken.totalReserves()
 * Balance: safeBox.balanceOf(address)
 */
export const useEarnPool = (
  token: Token
): {
  totalReserves: BN;
  totalSupply: BN;
  totalBorrows: BN;
  utilizationRate: BN;
  projectedAPY: BN;
} | null => {
    const { kit, network } = useContractKit();
    console.log(token)
    const bank = React.useMemo(() => (new kit.web3.eth.Contract(
        IBANK_ABI.abi as AbiItem[],
        Bank[network.chainId]
    ) as unknown) as HomoraBank, [kit, network]); 

    const call = React.useCallback(async () => {
        const bankInfo =  await bank.methods.banks(token.address).call();
        const cToken = (new kit.web3.eth.Contract(
            CERC20_ABI as AbiItem[],
            bankInfo.cToken
        ) as unknown) as CErc20Immutable;
        const totalSupply = await cToken.methods.totalSupply().call();
        const totalBorrows = await cToken.methods.totalBorrows().call();
        const totalReserves = await cToken.methods.totalReserves().call();
        return {
            totalSupply,
            totalBorrows,
            totalReserves,
        }
    }, [bank, token.address])

    const [tokenInfo] = useAsyncState(null, call);

    if (tokenInfo == null) return null;

    const {
        totalSupply,
        totalBorrows, 
        totalReserves,
    } = tokenInfo;

    return {
        totalSupply: toBN(totalSupply),
        totalBorrows: toBN(totalBorrows),
        totalReserves: toBN(totalReserves),
        utilizationRate: toBN(0),
        projectedAPY: toBN(0),
    };

