import React from "react";
import { useContractKit } from "@celo-tools/use-contractkit";
import { useParams, useHistory } from "react-router-dom";
import {
  Box,
  Button,
  Card,
  Flex,
  Heading,
  Input,
  Spinner,
  Text,
} from "theme-ui";
import { ethers } from "ethers";
import { BlockText } from "src/components/BlockText";
import { COLLATERAL_TOKENS, DEFAULT_GAS_PRICE, FEE_MODULE_V1 } from "src/config";
import { AbiItem, toBN, toWei, fromWei, isAddress } from "web3-utils";
import { toastTx } from "src/utils/toastTx";
import { toast } from "react-toastify";
import { CaretLeft, ArrowDown } from "phosphor-react";
import { YEAR_IN_SECONDS, ZERO_ADDRESS } from "src/constants";
import { useCUSD } from "src/hooks/useCUSD";
import { StableToken } from "@celo/contractkit";
import { MaxUint256 } from "@ethersproject/constants";
import { TokenInfo } from "src/components/TokenInfo";
import ERC20_ABI from "src/abis/fountain_of_youth/ERC20.json";


export const Supply: React.FC = () => {

  const { getConnectedKit, network, kit, address } = useContractKit();
  const [years, setYears] = React.useState("1");
  const [cost, setCost] = React.useState("5");
  const [approveLoading, setApproveLoading] = React.useState(false);
  const [reserveLoading, setReserveLoading] = React.useState(false);
  const [cUSD, refetchCUSD] = useCUSD();
  const history = useHistory();
  const { tokenAddress } = useParams<{ tokenAddress: string}>();


  const ERCCall = React.useCallback(async () => {
    const ERCToken = new kit.web3.eth.Contract(
      ERC20_ABI as AbiItem[],
      tokenAddress
    );
    if (!tokenAddress || !isAddress(tokenAddress)) {
      return ["0", "0"];
    }
    return await Promise.all([
      stakeToken.methods.balanceOf(address).call(),
      stakeToken.methods
        .allowance(address, STAKE_MAP[network.chainId].stakeRewards)
        .call(),
    ]);
  }, [kit, address, network]);
  const [
    [stakeTokenBalance, stakeTokenAllowance],
    refetchStakeToken,
  ] = useAsyncState(["0", "0"], stakeTokenCall);


  const approveButton = (
    <Button
      onClick={async () => {
        const kit = await getConnectedKit();
        // kit is connected to a wallet
        try {
          setApproveLoading(true);
          const cUSD = await kit._web3Contracts.getStableToken(
            StableToken.cUSD
          );
          const tx = await cUSD.methods
            .approve(FEE_MODULE_V1, MaxUint256.toString())
            .send({
              from: kit.defaultAccount,
              gasPrice: DEFAULT_GAS_PRICE,
              gas: 2e7,
            });
          toastTx(tx.transactionHash);
          refetchCUSD();
        } catch (e) {
          toast(e.message);
        } finally {
          setApproveLoading(false);
        }
      }}
    >
      Approve
    </Button>
  );

  const reserveButton = (
    <Button
      onClick={async () => {
        const kit = await getConnectedKit();}}
        // kit is connected to a wallet
        // const nom = new kit.web3.eth.Contract(
        //   NomMetadata.abi as AbiItem[],
        //   NOM[network.chainId]
        // ) as unknown as Nom;

    //     try {
    //       setReserveLoading(true);
    //       const tx = await nom.methods
    //         .reserve(
    //           ethers.utils.formatBytes32String(nameFormatted),
    //           Math.floor(Number(years) * YEAR_IN_SECONDS)
    //         )
    //         .send({
    //           from: kit.defaultAccount,
    //           gasPrice: DEFAULT_GAS_PRICE,
    //           gas: 2e7,
    //         });
    //       toastTx(tx.transactionHash);
    //       refetchNom();
    //     } catch (e) {
    //       toast(e.message);
    //     } finally {
    //       setReserveLoading(false);
    //     }
    //   }}
    >
      Reserve
    </Button>
  );

  const loading = approveLoading || reserveLoading;
  let button = approveButton;
  if (cUSD) {
    const fmtCost = cost === "" ? "0" : cost;
    const costBN = toBN(toWei(fmtCost));
    if (cUSD.balance.lt(costBN)) {
      button = <Button disabled={true}>Insufficient funds</Button>;
    } else if (cUSD.allowance.gt(costBN)) {
      button = reserveButton;
    }
  }

  let token = COLLATERAL_TOKENS.filter( ({ address }) => address.toLowerCase() === tokenAddress.toLowerCase())
  if (token.length !== 1) return null

  return (
    <Flex sx={{ alignItems: "center", flexDirection: "column" }}>
      <Card sx={{ width: "100%", maxWidth: "800px" }} py={4} px={3}>
        <Flex
          onClick={() => history.goBack()}
          sx={{ alignItems: "center", cursor: "pointer" }}
          mb={4}
        >
          <CaretLeft size={28} />
          <Text>Back</Text>
        </Flex>
        <Flex mb={4}>
          <Heading as="h2" mr={2}>
            Supply
          </Heading>
        </Flex>
        <Flex sx={{ alignItems: "center" }}>
          <Box sx={{ width: "100%" }} mr={2}>
            <Flex sx={{ justifyContent: "space-between", mb: 1 }}>
              <Text variant="form">I'd like to supply</Text>
              <Text
                sx={{ color: "primaryText", cursor: "pointer" }}
                variant="form"
                onClick={() => {
                  if (cUSD) {
                    const cost = fromWei(cUSD.balance);
                    setCost(cost);
                    setYears(
                      (
                        Number(cost) /
                        YEAR_IN_SECONDS
                      ).toString()
                    );
                  }
                }}
              >
                max: {cUSD ? Number(fromWei(cUSD.balance)).toFixed(4) : "0"}
              </Text>
            </Flex>
            <Input
              type="number"
              value={years}
              onChange={(e) => {
                const years = e.target.value;
                setYears(years);
                setCost(
                  (
                    Number(years) *
                    YEAR_IN_SECONDS
                  ).toString()
                );
              }}
              mr={2}
            />
          </Box>
        </Flex>
        <Flex sx={{ justifyContent: "center", mt: 6}}>
          <TokenInfo token={token[0]!} />
        </Flex>
        <Flex sx={{ justifyContent: "center", mt: 6 }}>
            {loading ? (
              <Spinner />
            ) : (
              button
            )}
        </Flex>
      </Card>
    </Flex>
  );
};
