import React from "react";
import { Flex } from "theme-ui";
import { BlockText } from "src/components/BlockText";
import { FarmView } from "src/pages/Farm/FarmTable"
import {  atom,  selector,  useRecoilState,  useRecoilValue,} from 'recoil';
import { Container } from "src/components/Container";
import { Token } from "src/utils/token";
import { Supply } from "src/pages/Farm/Supply";


export enum Page {
  Table,
  Supply, 
  Borrow, 
  Confirm,
}

export const pageState = atom({
  key: 'pageState',
  default: Page.Table
})

export interface poolProps {
  name: string;
  wstaking: string | null;
  spell: string;
  lp: string;
  tokens: Token[];
}

const emptyPoolState : poolProps = {
  name: "",
  wstaking: null,
  spell: "",
  lp: "",
  tokens: [],
}

export const poolState = atom({
  key: 'poolState',
  default: emptyPoolState
})

export const Farm: React.FC = () => {
  const [page] = useRecoilState(pageState);

  return (
    <div>
      {
      page === Page.Table ? (
          <Flex sx={{ flexDirection: "column"}}>
          <Flex
            sx={{
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              mt: ["33%", "10%"],
            }}
          >
            <BlockText variant="primary">
              Farm
            </BlockText>
            {/* TODO: format and make dynamic */}
            <BlockText>
              Unlock leverage up to 9x.
            </BlockText>
          </Flex>
          <Container>
              <FarmView />
          </Container>
          </Flex>
        ) : (
          page === Page.Supply ? (
            <Supply />

          ) : (
            <span>here</span>
          )
        )}
    </div>

  );
};
