import { css } from "@emotion/react";
import styled from "@emotion/styled";
import { useContractKit } from "@celo-tools/use-contractkit";
import { Button } from "theme-ui";
import { TokenInfo } from "src/components/TokenInfo";
import { Token } from "src/utils/token";
import { useHistory } from "react-router-dom";
import { AbiItem, toBN } from "web3-utils";
import BANK_ABI from "src/abis/dahlia_contracts/HomoraBank.json";
import CERC20_ABI from "src/abis/fountain_of_youth/CErc20Immutable.json";
import JUMPRATE_ABI from "src/abis/fountain_of_youth/JumpRateModelV2.json"; 
import { HomoraBank } from "src/generated/HomoraBank";
import { CErc20Immutable } from "src/generated/CErc20Immutable";
import { JumpRateModelV2 } from "src/generated/JumpRateModelV2";
import { Bank } from "src/config";
import React from "react";
import { useAsyncState } from "src/hooks/useAsyncState";
import { getAddress } from "ethers/lib/utils";
import { humanFriendlyWei } from "src/utils/eth";
import { PoolIcon } from "src/components/PoolIcon";
import { FarmInfo } from "src/components/FarmInfo";
import { Flex } from "theme-ui";
import { TokenBorrowInfo } from "src/components/TokenBorrowInfo";
import { Page } from "src/pages/Farm/FarmTable";
import { pageState } from "src/pages/Farm/FarmTable";
import {  RecoilRoot,  atom,  selector,  useRecoilState,  useRecoilValue } from 'recoil';


interface Props {
  name: string;
  wstaking: string | null;
  spell: string;
  tokens: Token[];
}

export const FarmPool: React.FC<Props> = ({ name, wstaking, spell, tokens }: Props) => {
  const [page, setPage] = useRecoilState(pageState);
  return (
    <Row>
      <td>
      <FarmInfo name={name} wstaking={wstaking} spell={spell} tokens={tokens}  />
      </td>
      <td>100%</td>
      <td>10%</td>
      <td>
        <Flex sx={{ flexDirection: "column", gap: "6px"}}>
          <span>Trading Fees: 10%</span>
          <span>Yield Farming: 0%</span>
        </Flex>
      </td>
      <td>
        <Flex sx={{ flexDirection: "column", alignItems: "center", gap: "6px"}}>
          {tokens.map((tok) => (
            <TokenBorrowInfo token={tok} apy="1" />
          ))}
        </Flex>
      </td>
      <td>10x</td>
      <td
        css={css`
          text-align: right;
        `}
      >
        <Button onClick={() => {
          setPage(Page.Supply)
        }}>Farm</Button>
      </td>
    </Row>
  );
};

const Row = styled.tr`
  height: 72px;
`;
