import { css } from "@emotion/react";
import styled from "@emotion/styled";
import { useContractKit } from "@celo-tools/use-contractkit";
import { TokenInfo } from "src/components/TokenInfo";
import { Token } from "src/utils/token";
import { useHistory } from "react-router-dom";
import { AbiItem, toBN } from "web3-utils";
import BANK_ABI from "src/abis/dahlia_contracts/HomoraBank.json";
import CERC20_ABI from "src/abis/fountain_of_youth/CErc20Immutable.json";
import JUMPRATE_ABI from "src/abis/fountain_of_youth/JumpRateModelV2.json"; 
import PROXYORACLE_ABI from "src/abis/dahlia_contracts/ProxyOracle.json";
import { HomoraBank } from "src/generated/HomoraBank";
import { ProxyOracle } from "src/generated/ProxyOracle";
import { CErc20Immutable } from "src/generated/CErc20Immutable";
import { JumpRateModelV2 } from "src/generated/JumpRateModelV2";
import { Bank, DEFAULT_GAS_PRICE} from "src/config";
import React from "react";
import { getAddress } from "ethers/lib/utils";
import { humanFriendlyWei } from "src/utils/eth";
import { PoolIcon } from "src/components/PoolIcon";
import { FarmInfo } from "src/components/FarmInfo";
import { Flex } from "theme-ui";
import { TokenBorrowInfo } from "src/components/TokenBorrowInfo";
import {  RecoilRoot,  atom,  useRecoilState,  useSetRecoilState } from 'recoil';
import { pageState, Page, poolProps, poolState } from "src/pages/Farm/newFarm/NewFarm";
import UNI_SPELL from "src/abis/dahlia_contracts/UniswapV2SpellV1.json";
import { UniswapV2SpellV1 } from "src/generated/UniswapV2SpellV1";
import { MaxUint256 } from "@ethersproject/constants";
import { toastTx } from "src/utils/toastTx";
import { toast } from "react-toastify";
import { posPageState, posPoolState, posPage } from "src/pages/Position";
import BN from 'bn.js';


export interface farmProps {
  tokenSupply: BN[] | null;
  lpSupply: BN | null;
  tokenBorrow: BN[] | null;
  lpBorrow: BN | null;
  info : any | null; 
  positionId: number | null;
  collateralSize: string | null; 
  existingBalance: BN[] | null; 
  remove: BN[] | null; 
  payback: BN[] | null; 
}

const emptyFarmState : farmProps = {
  tokenSupply: null,
  lpSupply: null,
  tokenBorrow: null,
  lpBorrow: null,
  info: null,
  positionId: null,
  collateralSize: null, 
  existingBalance: null, 
  remove: null,
  payback: null,
}

export const posFarmState = atom({
  key: 'posFarmState',
  default: emptyFarmState
})

interface Props {
  pool :poolProps,
  positionId: number,
  collateralSize: string, 
}

export const PositionEntry: React.FC<Props> = (info: Props) => {
  const setPosPage = useSetRecoilState(posPageState);
  const [pool, setPool] = useRecoilState(posPoolState)
  const [posFarm, setPosFarm] = useRecoilState(posFarmState);
  // setPool(info.pool); 
//   const setTab = useSetRecoilState(showTabs);

  const { kit, network, getConnectedKit } = useContractKit();
  const [confirmLoading, setConfirmLoading] = React.useState(false);

//   const bank = React.useMemo(() => (new kit.web3.eth.Contract(
//     BANK_ABI.abi as AbiItem[],
//     getAddress(Bank[network.chainId])
//   ) as unknown) as HomoraBank, [kit, network]); 

//   const call = React.useCallback(async () => {
//     try {
//         const borrows = [];
//         const factors = [];
//         const oracle = await bank.methods.oracle().call();
//         const proxyOracle = (new kit.web3.eth.Contract(
//           PROXYORACLE_ABI.abi as AbiItem[],
//           oracle,
//         ) as unknown) as ProxyOracle;
//         for (let i: number = 0; i < pool.tokens.length; i += 1) {
//           const bankInfo =  await bank.methods.getBankInfo(pool?.tokens[i]!.address).call();
//           const factor = await proxyOracle.methods.tokenFactors(pool?.tokens[i]!.address).call();
//           factors.push(factor);
//           const cToken = (new kit.web3.eth.Contract(
//             CERC20_ABI as AbiItem[],
//             bankInfo.cToken,
//           ) as unknown) as CErc20Immutable;
//           const totalSupply = await cToken.methods.totalSupply().call();
//           const totalBorrows = await cToken.methods.totalBorrows().call();
//           const totalReserves = await cToken.methods.totalReserves().call();
//           const interestRateModel = await cToken.methods.interestRateModel().call();
//           const jumpRate = (new kit.web3.eth.Contract(
//             JUMPRATE_ABI as AbiItem[],
//             interestRateModel,
//           ) as unknown) as JumpRateModelV2;
//           const borrowRate = await jumpRate.methods.getBorrowRate(totalSupply, totalBorrows, totalReserves).call();
//           borrows.push(toBN(borrowRate));
//         }
//         return {
//           borrowRate: borrows,
//           tokenFactor: factors,
//         };
//     } catch (error) {
//         console.log(error)
//     }

// }, [bank, pool.tokens, kit])

//   const [info] = useAsyncState(null, call);

//   const lever = (factor: {borrowFactor: string, collateralFactor: string}) => {
//     return 1 + (Number(factor.collateralFactor) / (Number(factor.borrowFactor) - Number(factor.collateralFactor)))
//   }

//   const maxLever = info ? Math.max(...(info?.tokenFactor.map((x) => lever({borrowFactor: x.borrowFactor, collateralFactor: x.collateralFactor})))!) : 0;


//   const { tokens } = poolInfo;

  const closeButton = (
    <Button
      onClick={async () => {
        const kit = await getConnectedKit();
        // kit is connected to a wallet
        try {
          setConfirmLoading(true);
          const bank = (new kit.web3.eth.Contract(
            BANK_ABI.abi as AbiItem[],
            getAddress(Bank[network.chainId])
            ) as unknown) as HomoraBank;
          const spell = (new kit.web3.eth.Contract(
            UNI_SPELL.abi as AbiItem[],
            getAddress(info.pool.spell),
          ) as unknown) as UniswapV2SpellV1;
          const bytes = spell.methods.removeLiquidityWERC20(
            info.pool.tokens[0]!.address, 
            info.pool.tokens[1]!.address, 
            [
              MaxUint256.toString(),
              0, 
              MaxUint256.toString(), 
              MaxUint256.toString(), 
              0, 
              0, 
              0
            ],
          ).encodeABI()
          const tx = await bank.methods
            .execute(
                0,
                info.pool.spell,
                bytes,
            ).send({
              from: kit.defaultAccount,
              gasPrice: DEFAULT_GAS_PRICE,
              gas: 10000000,
            });
          toastTx(tx.transactionHash);
        } catch (e) {
          toast(e.message);
        } finally {
          setConfirmLoading(false);
        }
      }}
    >
      Close
    </Button>
  );

  return (
    <Row>
      <td>
      <FarmInfo props={info.pool} />
      </td>
      <td>A lot</td>
      <td>A lot</td>
      <td>High</td>
      <td>High</td>
      <td
        css={css`
          text-align: right;
        `}
      >
        <Flex sx={{justifyContent: "center", alignItems: "center", flexDirection: "column", gap: "6px"}}>
          <Flex sx={{justifyContent: "center", alighnItems: "center", gap: "6px"}}>
            <Button onClick={() => {
              setPool(info.pool)
              setPosFarm({
                ...posFarm, 
                positionId: info.positionId,
