import { ChainId } from "@celo-tools/use-contractkit";
import { toWei } from "web3-utils";
import { Token } from "src/utils/token";
import { getAddress } from "ethers/lib/utils";


export const Bank = {
  [ChainId.Mainnet]: "0x10029F4FD22caD3fa9D29304B17a30b3C9769fD4",
  [ChainId.Alfajores]: "0x36C976Da6A6499Cad683064F849afa69CD4dec2e",
  [ChainId.Baklava]: "",
};
export const NOM = {
  [ChainId.Mainnet]: "0x10029F4FD22caD3fa9D29304B17a30b3C9769fD4",
  [ChainId.Alfajores]: "0x36C976Da6A6499Cad683064F849afa69CD4dec2e",
  [ChainId.Baklava]: "",
};

export const COLLATERAL_TOKENS = [
  new Token({
    address: getAddress("0x471EcE3750Da237f93B8E339c536989b8978a438"),
    name: "Celo",
    symbol: "CELO",
    decimals: 18,
    chainId: ChainId.Mainnet,
  }),
  new Token({
    address: getAddress("0x00be915b9dcf56a3cbe739d9b9c202ca692409ec"),
    name: "Ubeswap",
    symbol: "UBE",
    decimals: 18,
    chainId: ChainId.Mainnet,
  }),
  new Token({
    address: getAddress("0x64defa3544c695db8c535d289d843a189aa26b98"),
    name: "Moola cUSD AToken",
    symbol: "mcUSD",
    decimals: 18,
    chainId: ChainId.Mainnet,
  }),
  new Token({
    address: getAddress("0xa8d0e6799ff3fd19c6459bf02689ae09c4d78ba7"),
    name: "Moola cEUR AToken",
    symbol: "mcEUR",
    decimals: 18,
    chainId: ChainId.Mainnet,
  }),
  new Token({
    address: getAddress("0x2879bfd5e7c4ef331384e908aaa3bd3014b703fa"),
    name: "Savings CELO",
    symbol: "sCELO",
    decimals: 18,
    chainId: ChainId.Mainnet,
  }),
];

//collateral type to safebox
export const safeBoxMap = new Map<string, string>([
  ["0x471ece3750da237f93b8e339c536989b8978a438", "0x385aeb234131175993828061D58D32EdE98ccd78"]
]);

export const FEE_MODULE_V1 = "0x07DDCB69Bc2637A6c03d5523696E21B688b42d65";

export const DEFAULT_GAS_PRICE = toWei("0.13", "gwei");
