import React from "react";
import {
  Button,
  Card,
  Flex,
  Heading,
  Text,
} from "theme-ui";
import { CaretLeft } from "phosphor-react";

import { useERC } from "src/hooks/useERC";
import { useERCmulti } from "src/hooks/useERCmulti";
import { TokenInputForm } from "src/components/TokenInputForm";
import {  RecoilRoot,  atom,  selector,  useSetRecoilState, useRecoilState, useRecoilValue } from 'recoil';
import { pageState, Page, poolProps, poolState } from "src/pages/Farm";
import { showTabs } from "src/components/Header";
import { BlockText } from "src/components/BlockText";
import { Token } from "src/utils/token";
import { lpToken } from "src/config";
import { TokenAmountInfo } from "src/components/TokenAmountInfo";
import { farmState } from "src/pages/Farm/Supply";
import { fromWei, toWei, toBN } from "web3-utils";
import { TokenSlider } from "src/components/TokenSlider";



export const Confirm: React.FC = () => {
  const [pool] = useRecoilState(poolState);
  const setPage = useSetRecoilState(pageState)
  const [farm, setFarm] = useRecoilState(farmState);


  const lpTok: Token = new Token({
    ...lpToken,
    address: pool.lp,
  });

  const [tokenStates, refetchTokenStates] = useERCmulti(pool.tokens)
  const [amounts, setAmounts] = React.useState(Array(pool.tokens.length).fill(".7"))
  const [erc, refetchERC] = useERC(lpTok.address);

  const continueButton = (
    <Button
      onClick={() => {
        setFarm({
          ...farm,
          tokenBorrow: amounts.map((x) => toBN(toWei(String(x)))),
          });
        setPage(Page.Confirm); 
      }}
      >
        Continue
      </Button>
  )

  return (
    <Flex sx={{ alignItems: "center", flexDirection: "column" }}>
      <Card sx={{ width: "100%", maxWidth: "800px" }} py={4} px={3}>
        <Flex
          onClick={() => {
            setPage(Page.Borrow);
          }}
          sx={{ alignItems: "center", cursor: "pointer" }}
          mb={4}
        >
          <CaretLeft size={28} />
          <Text>Back</Text>
        </Flex>
        <Flex mb={4}>
          <Heading as="h2" mr={2}>
            Position Breakdown
          </Heading>
        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
          <BlockText variant="primary">I'm Supplying</BlockText>
          <Flex sx={{ justifyContent: "left", gap: "8px", alignItems: "center"}}>
            {pool.tokens.map((tok, index) => 
            <Flex
            sx={{
              alignItems: "center",
              mr: 4,
              padding:2,
              borderStyle: "solid",
              borderRadius: "10px",
            }}
          >
              <TokenAmountInfo key={tok.address} token={tok} amount={fromWei(farm.tokenSupply![index]!)} />       
            </Flex>
          )}
            <Flex
            sx={{
              alignItems: "center",
              mr: 4,
              padding:2,
              borderStyle: "solid",
              borderRadius: "10px",
            }}
            >
              <TokenAmountInfo key={lpTok.address} token={lpTok} amount={fromWei(farm.lpSupply!)} />       
            </Flex>
          </Flex>
        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
          <BlockText variant="primary">I'm Borrowing</BlockText>
          <Flex sx={{ justifyContent: "left", gap: "8px", alignItems: "center"}}>
            {pool.tokens.map((tok, index) => 
            <Flex
            sx={{
              alignItems: "center",
              mr: 4,
              padding:2,
              borderStyle: "solid",
              borderRadius: "10px",
            }}
          >
              <TokenAmountInfo key={tok.address} token={tok} amount={fromWei(farm.tokenBorrow![index]!)} />       
            </Flex>
          )}
          </Flex>
        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
          <BlockText variant="primary">Position Statistics</BlockText>
          <Flex sx={{ justifyContent: "left", gap: "8px", alignItems: "center"}}>
          </Flex>
        </Flex>
      </Card>
    </Flex>
  );
};
