import { css } from "@emotion/react";
import styled from "@emotion/styled";
import { Button } from "theme-ui";
import { TokenAmountDisplay } from "src/components/common/TokenAmountDisplay";
import { TokenInfo } from "src/components/common/TokenInfo";
import { Token } from "src/utils/token";

import { useEarnPool } from "./useEarnPool";

interface Props {
  token: Token;
  onSelect: (token: Token, action: "supply" | "withdraw") => void;
}

export const EarnPool: React.FC<Props> = ({ token, onSelect }: Props) => {
  // const info = useEarnPool(token);
  const info = null;
  return (
    <Row>
      <td>
        <TokenInfo token={token} />
      </td>
      <td>{info?.projectedAPY.toString().concat("%") ?? "--"}</td>
      <td>{info ? <TokenAmountDisplay amount={info.totalSupply} /> : "--"}</td>
      <td>{info ? <TokenAmountDisplay amount={info.totalBorrows} /> : "--"}</td>
      <td>{info ? <TokenAmountDisplay amount={info.totalBorrows} /> : "--"}</td>
      <td
        css={css`
          text-align: right;
        `}
      >
        <div
          css={css`
            display: flex;
            gap: 8px;
            justify-content: flex-end;
          `}
        >
          <Button onClick={() => onSelect(token, "supply")}>Supply</Button>
          <Button onClick={() => onSelect(token, "withdraw")}>Withdraw</Button>
        </div>
      </td>
    </Row>
  );
};

const Row = styled.tr`
  height: 72px;
`;
