import { css } from "@emotion/react";
import styled from "@emotion/styled";
import { useState } from "react";
import { Token } from "src/utils/token";

import { EarnPool } from "./EarnPool";
import { COLLATERAL_TOKENS } from "src/config";

// TODO(igm): need to add the tokens

export const EarnView: React.FC = () => {
  const [info, setInfo] = useState<{
    token: Token;
    action: "supply" | "withdraw";
  } | null>(null);

  const onSelect = (theToken: Token, action: "supply" | "withdraw") => {
    setInfo({
      token: theToken,
      action,
    });
  };

  return (
        <Table>
          <thead>
            <tr>
              <th
                css={css`
                  text-align: left !important;
                `}
              >
                Asset
              </th>
              <th>APY</th>
              <th>Total Supply</th>
              <th>Total Borrow</th>
              <th>Utilization</th>
              <th />
            </tr>
          </thead>
          <tbody>
            {COLLATERAL_TOKENS.map((token) => (
              <EarnPool key={token.address} token={token} onSelect={onSelect} />
            ))}
          </tbody>
        </Table>
  );
};

const Table = styled.table`
  th {
    color: #546e7a;
    font-weight: 600;
    font-size: 14px;
  }
  td {
    color: #fff;
    font-size: 16px;
    line-height: 20px;
  }
  th,
  td {
    text-align: center;
  }
  width: 100%;
`;
