import React from "react";
import { useContractKit } from "@celo-tools/use-contractkit";
import ERC20_ABI from "src/abis/fountain_of_youth/ERC20.json";
import IERC20_ABI from "src/abis/dahlia_contracts/IERC20.json"
import { useAsyncState } from "src/hooks/useAsyncState";
import { isAddress, toBN, AbiItem } from "web3-utils";
import { getAddress } from "ethers/lib/utils";
import { safeBoxMap } from "src/config";

export const useERC = (tokenAddress: string) => {
  const { kit, address } = useContractKit();
  const call = React.useCallback(async () => {
    if (!tokenAddress || !isAddress(tokenAddress)) {
      return ["0", "0"];
    }
    const ERCToken = new kit.web3.eth.Contract(
      IERC20_ABI.abi as AbiItem[],
      getAddress(tokenAddress)
    );    
    return await Promise.all([
      ERCToken.methods.balanceOf(address).call(),
      ERCToken.methods
        .allowance(address, safeBoxMap.get(tokenAddress))
        .call(),
    ]);
  }, [kit, address, tokenAddress]);
  return useAsyncState(null, call);
};
