import React from "react";
import { useContractKit } from "@celo-tools/use-contractkit";
import { useParams, useHistory } from "react-router-dom";
import {
  Box,
  Button,
  Card,
  Flex,
  Heading,
  Input,
  Spinner,
  Text,
} from "theme-ui";
import { COLLATERAL_TOKENS, DEFAULT_GAS_PRICE, safeBoxMap } from "src/config";
import { AbiItem, toBN, toWei, fromWei } from "web3-utils";
import { toastTx } from "src/utils/toastTx";
import { toast } from "react-toastify";
import { CaretLeft } from "phosphor-react";
import { DahliaTokenInfo } from "src/components/DahliaTokenInfo";
import { TokenAmountInfo } from "src/components/TokenAmountInfo"
import SAFEBOX_ABI from "src/abis/dahlia_contracts/SafeBox.json";
import { SafeBox } from "src/generated/SafeBox";
import { humanFriendlyWei } from "src/utils/eth";
import { getAddress } from "ethers/lib/utils";
import { useSafeBox } from "src/hooks/useSafeBox";
import { TokenInputForm } from "src/components/TokenInputForm";

export const Withdraw: React.FC = () => {
  // TODO: fix exchange rate and decimals

  const { getConnectedKit } = useContractKit();
  const [amount, setAmount] = React.useState("1");
  const [withdrawLoading, setWithdrawLoading] = React.useState(false);
  const { tokenAddress } = useParams<{ tokenAddress: string}>();
  const [safeBox, refetchSafeBox] = useSafeBox(safeBoxMap.get(tokenAddress)!);

  const history = useHistory();

  const withdrawButton = (
    <Button
      onClick={async () => {
        const kit = await getConnectedKit();
        // kit is connected to a wallet
        const safeBox = (new kit.web3.eth.Contract(
          SAFEBOX_ABI.abi as AbiItem[],
          safeBoxMap.get(getAddress(tokenAddress))!,
        ) as unknown) as SafeBox;
        try {
          setWithdrawLoading(true);
          const tx = await safeBox.methods
            .withdraw(
              toWei(amount)
            ).send({
              from: kit.defaultAccount,
              gasPrice: DEFAULT_GAS_PRICE,
            });
          toastTx(tx.transactionHash);
          refetchSafeBox();
        } catch (e) {
          toast(e.message);
        } finally {
          setWithdrawLoading(false);
        }
      }}
    >
      Withdraw
    </Button>
  );

  const loading =  withdrawLoading;
  let button = withdrawButton;
  if (safeBox) {
    const fmtCost = amount === "" ? "0" : amount;
    const amountBN = toBN(toWei(fmtCost));
    if (safeBox.balance.lt(amountBN)) {
      button = <Button disabled={true}>Insufficient funds</Button>;
    }
  }

  let token = COLLATERAL_TOKENS.filter( ({ address }) => address.toLowerCase() === tokenAddress.toLowerCase())
  if (token.length !== 1) return null;

  return (
    <Flex sx={{ alignItems: "center", flexDirection: "column" }}>
      <Card sx={{ width: "100%", maxWidth: "800px" }} py={4} px={3}>
        <Flex
          onClick={() => history.goBack()}
          sx={{ alignItems: "center", cursor: "pointer" }}
          mb={4}
        >
          <CaretLeft size={28} />
          <Text>Back</Text>
        </Flex>
        <Flex mb={4}>
          <Heading as="h2" mr={2}>
            Withdraw
          </Heading>
        </Flex>
        {/* <Flex sx={{ alignItems: "center", width: "100%"}}> */}
          {/* <Box sx={{ width: "100%" }} mr={2}> */}
          <TokenInputForm key={token[0]!.address} token={token[0]!} amount={amount}
             setAmount={setAmount} 
             balance={safeBox ? safeBox.balance : null}
            />
          {/* </Flex> */}
            {/* <Flex sx={{ justifyContent: "space-between", mb: 1 }}>
              <Text variant="form">I'd like to withdraw</Text>
              <Text
                sx={{ color: "primaryText", cursor: "pointer" }}
                variant="form"
                onClick={() => {
                  if (safeBox) {
                    const cost = fromWei(safeBox.balance);
                    setAmount(cost);
                  }
                }}
              >
                max: {safeBox ? humanFriendlyWei(safeBox.balance) : "0"}
              </Text>
            </Flex>
            <Input
              type="number"
              value={amount}
              onChange={(e) => {
                setAmount(e.target.value);
              }}
              mr={2}
            />
          </Box>
        </Flex>
        <Flex sx={{ justifyContent: "center", mt: 6}}>
          <DahliaTokenInfo token={token[0]!} />
        </Flex> */}
        <Flex sx={{ justifyContent: "center", mt: 6, gap: "8px", alignItems: "center"}}>
          <span>I will receive</span>
          <Flex
          sx={{
            alignItems: "center",
            mr: 4,
            padding:2,
            borderStyle: "solid",
            borderRadius: "10px",
          }}
        >
          <TokenAmountInfo token={token[0]!} amount={amount} />
        </Flex>
        </Flex>
        <Flex sx={{ justifyContent: "center", mt: 6 }}>
            {loading ? (
              <Spinner />
            ) : (
              button
            )}
        </Flex>
      </Card>
    </Flex>
  );
};
