  colors: {
    dark: '#000000',
    gray: '#72828a',
    midDark: '#070a0e',
    mid2Dark: '#1c2127',
    rgbaShadow: 'rgba(128, 255, 219, 0.36)',
    sky: '#80ffdb',
    secondColor: '#3c9279',
    thirdColor: '#070a0e',
    fourthColor: '#72828a',
},
  fonts: {
    body: "Roboto",
    heading: "Roboto",
    monospace: "Roboto",
  },
  fontSizes: [12, 14, 16, 18, 20, 24, 28, 32, 48, 64, 96],
  lineHeights: {
    body: 1.5,
    heading: 1.25,
  },
  space: [0, 4, 8, 16, 24, 32, 48, 64],
  letterSpacings: {
    small: "-0.05em",
  },
  sizes: {
    avatar: 48,
  },
  buttons: {
    major: {
        color: 'sky',
        bg: 'thirdColor',
