import React from "react";
import { atom, useSetRecoilState, useRecoilState } from 'recoil';
import { getToken } from "src/utils/token";
import { useParams } from "react-router-dom";
import { poolState, poolProps } from "src/pages/Farm/newFarm/NewFarm";
import BN from 'bn.js';
import { Supply } from "src/pages/Position/Add/supply";
import { Borrow } from "src/pages/Position/Add/borrow";
// import { Confirm } from "src/pages/Position/Add/confirm";


export enum addPage {
    Supply, 
    Borrow, 
    Confirm,
}

export const addPageState = atom({
    key: 'addPageState',
    default: addPage.Supply
})

export interface addFarmProps {
    tokenSupply: BN[] | null;
    lpSupply: BN | null;
    tokenBorrow: BN[] | null;
    lpBorrow: BN | null;
    positionId: string | null;
    collateralSize: string | null; 
    existingBalance: BN[] | null; 
    supplyValue: number | null;
    borrowValue: number | null;
    debtRatio: number | null;
    lever: number | null;
  }

  const emptyFarmState : addFarmProps = {
    tokenSupply: null,
    lpSupply: null,
    tokenBorrow: null,
    lpBorrow: null,
    positionId: null,
    collateralSize: null, 
    existingBalance: null, 
    supplyValue: null,
    borrowValue: null,
    debtRatio: null,
    lever: null,
  }

  export const addFarmState = atom({
    key: 'addFarmState',
    default: emptyFarmState
  })

export const Add: React.FC = () => {
  const { positionId, collateralSize, name, wrapper, spell, lp, tokens } = useParams<{ positionId: string, collateralSize: string, name: string, wrapper: string, spell: string, lp:string, tokens:string}>();
  const setPool = useSetRecoilState(poolState); 
  const setFarm = useSetRecoilState(addFarmState)
  const set: poolProps  = {
      name: name,
      wrapper: wrapper,
      spell: spell,
      lp: lp,
      tokens: (tokens.split(',').map((x) => getToken(x)!)),
  }
  setPool(set);  
  setFarm({
      ...emptyFarmState,
      positionId, 
      collateralSize,
  })

  const [page] = useRecoilState(addPageState);

  return (
    <div>
      {
        page === addPage.Supply ? (
            <Supply />
          ) : (
            page === addPage.Borrow ? (
            <Borrow />
          ) : (
            <Borrow />
            // <Confirm />
          ))}
    </div>

  );
};
