import { Token } from "src/utils/token";
import {
    Box,
    Flex,
    Input,
    Text,
  } from "theme-ui";
import { humanFriendlyWei } from "src/utils/eth";
import { fromWei } from "web3-utils";
import { TokenInfo } from "src/components/TokenInfo";
import BN from 'bn.js';
import { Slider } from "rsuite";
import "rsuite/dist/styles/rsuite-default.min.css";


interface Props {
  token: Token;
  amount: string;
  setAmount: any,
  max: string;
}

export const TokenSlider: React.FC<Props> = ({ token, amount, setAmount, max}: Props) => {
  return (
    <Flex sx={{ alignItems: "center" }}>
    <Box sx={{ width: "100%" }} mr={2}>

      <Flex sx={{ justifyContent: "space-between", mb: 1, alignItems: "center"}}>
        <Flex sx={{ mb: 1 }}>
            <Slider
            progress
            defaultValue={50}
            style={{width: "100%"}}
            step={.01}
            max={Number(max)}
            />      
        </Flex>
        <TokenInfo token={token} />
      </Flex>   

    </Box>
    <div style={{ display: "block"}} >


    </div>
  </Flex>
  )
};
