import { isReactNative, isWsUrl, isLocalhostUrl, } from "@json-rpc-tools/utils";
const WS = typeof global.WebSocket !== "undefined" ? global.WebSocket : require("ws");
export class WsConnection {
    constructor(url) {
        this.url = url;
        this.events = new EventEmitter();
