import React from "react";
import {
  Button,
  Card,
  Flex,
  Heading,
  Text,
  Spinner,
} from "theme-ui";
import { COLLATERAL_TOKENS, DEFAULT_GAS_PRICE, safeBoxMap } from "src/config";
import { CaretLeft } from "phosphor-react";
import { useContractKit } from "@celo-tools/use-contractkit";
import { useERC } from "src/hooks/useERC";
import { useERCmulti } from "src/hooks/useERCmulti";
import { TokenInputForm } from "src/components/TokenInputForm";
import {  RecoilRoot,  atom,  selector,  useSetRecoilState, useRecoilState, useRecoilValue } from 'recoil';
import { pageState, Page, poolProps, poolState } from "src/pages/Farm";
import { showTabs } from "src/components/Header";
import { BlockText } from "src/components/BlockText";
import { Token } from "src/utils/token";
import { lpToken, Bank } from "src/config";
import { TokenAmountInfo } from "src/components/TokenAmountInfo";
import { farmState } from "src/pages/Farm/Supply";
import { fromWei, toWei, toBN, AbiItem } from "web3-utils";
import { toastTx } from "src/utils/toastTx";
import { toast } from "react-toastify";
import BANK_ABI from "src/abis/dahlia_contracts/HomoraBank.json";
import { HomoraBank } from "src/generated/HomoraBank";
import { getAddress } from "ethers/lib/utils";


export const Confirm: React.FC = () => {
  const { getConnectedKit, network } = useContractKit();
  const [approveLoading, setApproveLoading] = React.useState(false);
  const [confirmLoading, setConfirmLoading] = React.useState(false);
  const [pool] = useRecoilState(poolState);
  const setPage = useSetRecoilState(pageState)
  const [farm, setFarm] = useRecoilState(farmState);


  const lpTok: Token = new Token({
    ...lpToken,
    address: pool.lp,
  });

  const [tokenStates, refetchTokenStates] = useERCmulti(pool.tokens)
  const [amounts, setAmounts] = React.useState(Array(pool.tokens.length).fill(".7"))
  const [erc, refetchERC] = useERC(lpTok.address);

  const approveButton = (
    <Button
      onClick={async () => {
        const kit = await getConnectedKit();
        // kit is connected to a wallet
        try {
          setApproveLoading(true);
          const ERCToken = (new kit.web3.eth.Contract(
            ERC20_ABI as AbiItem[],
            tokenAddress
          ) as unknown) as ERC20; 
          const tx = await ERCToken.methods
            .approve(safeBoxMap.get(getAddress(tokenAddress))!, MaxUint256.toString())
            .send({
              from: kit.defaultAccount,
              gasPrice: DEFAULT_GAS_PRICE,
            });
          toastTx(tx.transactionHash);
          refetchERC();
        } catch (e) {
          toast(e.message);
        } finally {
          setApproveLoading(false);
        }
      }}
    >
      Approve
    </Button>
  );

  const confirmButton = (
    <Button
      onClick={async () => {
        const kit = await getConnectedKit();
        // kit is connected to a wallet
        const bank = (new kit.web3.eth.Contract(
            BANK_ABI.abi as AbiItem[],
            getAddress(Bank[network.chainId])
            ) as unknown) as HomoraBank;
        try {
          setSupplyLoading(true);
          const tx = await safeBox.methods
            .deposit(
              toWei(amount)
            ).send({
              from: kit.defaultAccount,
              gasPrice: DEFAULT_GAS_PRICE,
            });
          toastTx(tx.transactionHash);
          refetchERC();
        } catch (e) {
          toast(e.message);
        } finally {
          setSupplyLoading(false);
        }
      }}
    >
      Deposit
    </Button>
  );

  const loading = approveLoading || supplyLoading;
  let button = approveButton;
  if (erc) {
    const fmtCost = amount === "" ? "0" : amount;
    const amountBN = toBN(toWei(fmtCost));
    if (erc.balance.lt(amountBN)) {
      button = <Button disabled={true}>Insufficient funds</Button>;
    } else if (erc.allowance.gt(amountBN)) {
      button = supplyButton;
    }
  }

  return (
    <Flex sx={{ alignItems: "center", flexDirection: "column" }}>
      <Card sx={{ width: "100%", maxWidth: "800px" }} py={4} px={3}>
        <Flex
          onClick={() => {
            setPage(Page.Borrow);
          }}
          sx={{ alignItems: "center", cursor: "pointer" }}
          mb={4}
        >
          <CaretLeft size={28} />
          <Text>Back</Text>
        </Flex>
        <Flex mb={4}>
          <Heading as="h2" mr={2}>
            Position Breakdown
          </Heading>
        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
          <BlockText variant="primary">I'm Supplying</BlockText>
          <Flex sx={{ justifyContent: "left", gap: "8px", alignItems: "center"}}>
            {pool.tokens.map((tok, index) => 
            <Flex
            sx={{
              alignItems: "center",
              mr: 4,
              padding:2,
              borderStyle: "solid",
              borderRadius: "10px",
            }}
          >
              <TokenAmountInfo key={tok.address} token={tok} amount={fromWei(farm.tokenSupply![index]!)} />       
            </Flex>
          )}
            <Flex
            sx={{
              alignItems: "center",
              mr: 4,
              padding:2,
              borderStyle: "solid",
              borderRadius: "10px",
            }}
            >
              <TokenAmountInfo key={lpTok.address} token={lpTok} amount={fromWei(farm.lpSupply!)} />       
            </Flex>
          </Flex>
        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
          <BlockText variant="primary">I'm Borrowing</BlockText>
          <Flex sx={{ justifyContent: "left", gap: "8px", alignItems: "center"}}>
            {pool.tokens.map((tok, index) => 
            <Flex
            sx={{
              alignItems: "center",
              mr: 4,
              padding:2,
              borderStyle: "solid",
              borderRadius: "10px",
            }}
          >
              <TokenAmountInfo key={tok.address} token={tok} amount={fromWei(farm.tokenBorrow![index]!)} />       
            </Flex>
          )}
          </Flex>
        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
          <BlockText variant="primary">Position Statistics</BlockText>
          <Flex sx={{ justifyContent: "left", gap: "8px", flexDirection: "column"}}>
              <BlockText>Est Debt Ratio: </BlockText>
              <BlockText>Leverage </BlockText>
              <BlockText>Total Supply: </BlockText>
              <BlockText>Total Borrow: </BlockText>
              <BlockText>Position Value: </BlockText>
              <BlockText>Farming APY: </BlockText>
              <BlockText>Trading Fee APY: </BlockText>
              <BlockText>Borrow APY: </BlockText>
              <BlockText>Total APY: </BlockText>
              <BlockText>Slippage Tolerance: </BlockText>
              <BlockText>Price Impact: </BlockText>
          </Flex>
        </Flex>
        <Flex sx={{ justifyContent: "center", mt: 6 }}>
            {loading ? (
              <Spinner />
            ) : (
              button
            )}
        </Flex>
      </Card>
    </Flex>
  );
};
