import styled from "@emotion/styled";

export const Container = styled.article`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 3rem 0;
  @media screen and (max-width: 1000px) {
    margin: 2rem 0;
  }
`;

export const Title = styled.h1`
  color: var(--main-color);
  font-size: 48px;
  font-weight: bold;
  padding: 0;
  margin: 0;
  @media screen and (max-width: 1000px) {
    font-size: 42px;
  }
`;

export const Description = styled.h3`
  display: flex;
  align-items: center;
  color: var(--white);
  font-weight: 400;
  padding: 0;
  font-size: 24px;
  margin: 0;
  margin-top: 1rem;
  @media screen and (max-width: 1000px) {
    font-size: 18px;
  }
`;
