import React, { useEffect } from "react";
import { useContractKit } from "@celo-tools/use-contractkit";
import {
  Button,
  Card,
  Flex,
  Heading,
  Spinner,
  Text,
} from "theme-ui";
import { COLLATERAL_TOKENS, DEFAULT_GAS_PRICE, safeBoxMap } from "src/config";
import { AbiItem, toBN, toWei } from "web3-utils";
import { toastTx } from "src/utils/toastTx";
import { toast } from "react-toastify";
import { CaretLeft } from "phosphor-react";
import { MaxUint256 } from "@ethersproject/constants";
import ERC20_ABI from "src/abis/fountain_of_youth/ERC20.json";
import SAFEBOX_ABI from "src/abis/dahlia_contracts/SafeBox.json";
import { ERC20 } from "src/generated/ERC20";
import { SafeBox } from "src/generated/SafeBox";
import { useERC } from "src/hooks/useERC";
import { useERCmulti } from "src/hooks/useERCmulti";
import { getAddress } from "ethers/lib/utils";
import { TokenInputForm } from "src/components/TokenInputForm";
import {  RecoilRoot,  atom,  selector,  useSetRecoilState, useRecoilState } from 'recoil';
import { pageState, Page, farmProps, poolState } from "src/pages/Farm";
import { showTabs } from "src/components/Header";
import { BlockText } from "src/components/BlockText";
import { ChainId } from "@celo-tools/use-contractkit";
import { Token } from "src/utils/token";

export const Supply: React.FC = () => {
  const { getConnectedKit } = useContractKit();
  const [approveLoading, setApproveLoading] = React.useState(false);
  const [supplyLoading, setSupplyLoading] = React.useState(false);
  const [pool] = useRecoilState(poolState);
  const setPage = useSetRecoilState(pageState)
  const setTab = useSetRecoilState(showTabs);

  const [amount, setAmount] = React.useState("0");
  const tokenAddress = pool?.tokens[0]?.address!;

  useEffect(() => {
    console.log(amounts)
  }, [amounts]);

  const lpTok = new Token({
    address: pool.lp,
    name: "Ubeswap LP",
    symbol: "ULP",
    decimals: 18,
    chainId: ChainId.Mainnet,
    logoURI: "public/favicon.ico"
  });

  const [tokenStates, refetchTokenStates] = useERCmulti(pool.tokens)
  const [amounts, setAmounts] = React.useState(Array(pool.tokens.length).fill("0"))

  const [lpAmount, setLPAmount] = React.useState("0")
  const [erc, refetchERC] = useERC(lpTok.address);

  // console.log(tokenStates![0]?.allowance.toString())
  const approveButton = (
    <Button
      onClick={async () => {
        const kit = await getConnectedKit();
        // kit is connected to a wallet
        try {
          setApproveLoading(true);
          const ERCToken = (new kit.web3.eth.Contract(
            ERC20_ABI as AbiItem[],
            tokenAddress
          ) as unknown) as ERC20; 
          const tx = await ERCToken.methods
            .approve(safeBoxMap.get(getAddress(tokenAddress))!, MaxUint256.toString())
            .send({
              from: kit.defaultAccount,
              gasPrice: DEFAULT_GAS_PRICE,
              gas: 2e7,
            });
          toastTx(tx.transactionHash);
          refetchERC();
        } catch (e) {
          toast(e.message);
        } finally {
          setApproveLoading(false);
        }
      }}
    >
      Approve
    </Button>
  );

  const supplyButton = (
    <Button
      onClick={async () => {
        const kit = await getConnectedKit();
        // kit is connected to a wallet
        const safeBox = (new kit.web3.eth.Contract(
          SAFEBOX_ABI.abi as AbiItem[],
          safeBoxMap.get(getAddress(tokenAddress))!,
        ) as unknown) as SafeBox;
        try {
          setSupplyLoading(true);
          const tx = await safeBox.methods
            .deposit(
              toWei(amount)
            ).send({
              from: kit.defaultAccount,
              gasPrice: DEFAULT_GAS_PRICE,
              gas: 2e8,
            });
          toastTx(tx.transactionHash);
          refetchERC();
        } catch (e) {
          toast(e.message);
        } finally {
          setSupplyLoading(false);
        }
      }}
    >
      Deposit
    </Button>
  );

  const loading = approveLoading || supplyLoading;
  let button = approveButton;
  if (erc) {
    const fmtCost = amount === "" ? "0" : amount;
    const amountBN = toBN(toWei(fmtCost));
    if (erc.balance.lt(amountBN)) {
      button = <Button disabled={true}>Insufficient funds</Button>;
    } else if (erc.allowance.gt(amountBN)) {
      button = supplyButton;
    }
  }

  let token = COLLATERAL_TOKENS.filter( ({ address }) => address.toLowerCase() === tokenAddress.toLowerCase())
  if (token.length !== 1) return null;



  return (
    <Flex sx={{ alignItems: "center", flexDirection: "column" }}>
      <Card sx={{ width: "100%", maxWidth: "800px" }} py={4} px={3}>
        <Flex
          onClick={() => {
            setTab(true);
            setPage(Page.Table);
          }}
          sx={{ alignItems: "center", cursor: "pointer" }}
          mb={4}
        >
          <CaretLeft size={28} />
          <Text>Back</Text>
        </Flex>
        <Flex mb={4}>
          <Heading as="h2" mr={2}>
            Supply
          </Heading>
        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
          {pool.tokens.map((tok, index) => 
            <TokenInputForm key={tok.address} token={tok} amount={amounts[index]} setAmount={(s: string) => setAmounts(amounts.map((x, i) => i === index ? s : x))} balance={erc ? erc.balance : null}/>
          )}
        </Flex>
        <Flex sx={{mt: "25px"}}>
          <BlockText variant="primary">Supply LP Token</BlockText>
        </Flex>
        <TokenInputForm token={lpTok} amount={lpAmount} setAmount={setLPAmount} balance={erc ? erc.balance : null}/>
        <Flex sx={{ justifyContent: "center", mt: 6 }}>
            {loading ? (
              <Spinner />
            ) : (
              button
            )}
        </Flex>
      </Card>
    </Flex>
  );
};
