import { css } from "@emotion/react";
import { FarmPool } from "src/pages/Farm/FarmPool";
import { FARMS } from "src/config";
import { SimpleTable } from "src/components/SimpleTable"
import { useState } from "react";
import { EarnView } from "src/pages/Earn/EarnTable"
import {  RecoilRoot,  atom,  selector,  useRecoilState,  useRecoilValue,} from 'recoil';

export enum Page {
  Table,
  Supply, 
  Borrow, 
  Comfirm,
}

export const pageState = atom({
  key: 'pageState',
  default: Page.Table
})

export const FarmView: React.FC = () => {
  const [page] = useRecoilState(pageState);

  return (
    <RecoilRoot>
    { page === Page.Table ? 
      <SimpleTable>
          <thead>
            <tr>
              <th
                css={css`
                  text-align: left !important;
                `}
              >
                Pool
              </th>
              <th>Projected APY</th>
              <th>Pool APY</th>
              <th></th>
              <th>Borrow APY</th>
              <th>Max Leverage</th>
              <th />
            </tr>
          </thead>
          <tbody>
            {FARMS.map((farm) => (
              <FarmPool key={farm.name} name={farm.name} wstaking={farm.wstaking} spell={farm.spell} tokens={farm.tokens}/>
            ))}
          </tbody>
        </SimpleTable>
      :
      <EarnView />
    }
    </RecoilRoot>

  );
};
