import React from 'react';
import styled from '@emotion/styled';
import BackgroundImage from '../../images/background.png';
import { useContractKit } from "@celo-tools/use-contractkit";
import { AbiItem, toBN } from "web3-utils";
import { HomoraBank } from "src/generated/HomoraBank";
import BANK_ABI from "src/abis/dahlia_contracts/HomoraBank.json";
import { getAddress } from "ethers/lib/utils";
import { Bank } from "src/config";
import { useAsyncState } from "src/hooks/useAsyncState";
import { FARMS } from "src/config";
import { Spinner } from "theme-ui";
import { PositionEntry } from "src/pages/Position/PositionEntry"
import {
    Container, 
    Title, 
    Description,
    Wrapper,
    Background,
    CardContainer,
    InfoContainer
  } from "src/components/MainPages";
import Image from 'src/images/background.png';


const Position = () => {

  const { kit, address, network } = useContractKit();

  const bank = React.useMemo(() => (new kit.web3.eth.Contract(
    BANK_ABI.abi as AbiItem[],
    getAddress(Bank[network.chainId])
  ) as unknown) as HomoraBank, [kit, network]); 

  const call = React.useCallback(async () => {
    try {
      let posInfo = []
      const nextPositionId = await bank.methods.nextPositionId().call(); 
      for (let i = 1; i < Number(nextPositionId); i += 1) {
        posInfo.push({...await bank.methods.getPositionInfo(i).call(), positonId: i})
      }
      return posInfo;  
    } catch (error) {
        console.log(error)
    }  
}, [bank])

  const [info] = useAsyncState(null, call);
  return (
    <Wrapper>
      <Container>
        <Title>Positions</Title>
        <Description>Manage your positions with ease.</Description>
      </Container>
      <CardContainer>
      <InfoContainer>
      <HeaderContainer>
          <h2>Pool</h2>
          <h2 className='center'>Borrow</h2>
          <h2 className='center'>Current Value</h2>
          <h2 className='center'>Debt Ratio</h2>
          <h2 className='center'>Apy</h2>
          <div></div>
        </HeaderContainer>
        { info ? 
        info.filter((pos) => pos.owner === address)
        .flatMap((pos) => {
          const active = FARMS
        .filter((farm) => getAddress(farm.lp) === 
        getAddress(toBN(pos.collId).toString(16)))
        return {pos, active}
      }).map((info) => (
        <PositionEntry collateralSize={info.pos.collateralSize} positionId={info.pos.positonId} pool={info.active[0]!} />
      )) : 
      (<Spinner />)
      }
      </InfoContainer>
      </CardContainer>
      <Background src={Image} alt='Background Logo' />
    </Wrapper>
  );
};

export default Position;

const SecondHeader = styled.h2`
  font-size: 28px;
  font-weight: 600;
  color: var(--white);
  margin: 4rem 0 2rem;
  padding: 0;
`;

const Item = styled.div`
  width: 100%;
  padding: 0 2rem;
  display: grid;
  grid-template-columns: repeat(5, 0.95fr) 1.4fr;
  grid-template-rows: 1fr;
  height: 100px;
  border-bottom: 1px solid #72828a15;
  color: var(--white);
  .left {
    margin-left: 1rem;
  }
  .image {
    height: 40px;
    margin-right: 0.5rem;
  }
  h2,
  h3 {
    margin: 0;
    padding: 0;
  }
  .flexFirst {
    display: flex;
    align-items: center;
    height: 100%;
    h2 {
      font-size: 12px;
      color: var(--white);
      font-weight: 400;
    }
  }

  .centerMiddle {
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .thin {
    color: var(--white);
    font-size: 16px;
    font-weight: 400;
  }
  .end {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: repeat(2, 1fr);
    margin: auto 0;
  }
  button {
    box-shadow: 0px 0px 12px rgba(128, 255, 219, 0.36);
    border-radius: 5px;
    background: var(--main-color);
    color: #000;
    font-weight: 600;
    font-size: 14px;
    text-decoration: none;
    margin-bottom: 0.2rem;
  }
  .small {
    width: 85px;
    height: 30px;
  }
  .large {
    width: 115px;
    height: 30px;
  }
  .special {
    background: #1c2127;
    border: 1px solid var(--main-color);
    color: var(--main-color);
  }
  @media screen and (max-width: 1000px) {
    display: none;
  }
`;

const Header = styled.h1`
  font-size: 48px;
  font-weight: 700;
  color: var(--main-color);
  padding: 0;
  margin: 0;
  margin-bottom: 2rem;
  text-align: center;
`;

const Desc = styled.p`
  font-weight: 400;
  font-size: 22px;
  color: var(--white);
  padding: 0;
  margin: 1rem 0;
  text-align: center;
`;

const Button = styled.button`
  width: 382px;
  min-height: 122px;
  background: #1c2127;
  border: 4px solid var(--main-color);
  box-shadow: 0px 0px 12px rgba(128, 255, 219, 0.36);
  border-radius: 5px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-around;
  color: var(--main-color);
  margin: 2rem auto;
  h3 {
    font-weight: 700;
    font-size: 22px;
    padding: 0;
    margin: 0;
  }
  p {
    font-weight: 400;
    font-size: 36px;
    padding: 0;
    margin: 0;
  }
`;

const HeaderContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(5, 0.95fr) 1.4fr;
  grid-template-rows: 1fr;
  height: 55px;
  border-bottom: 1px solid #72828a15;
  margin: 0;
  padding: 0;
  padding: 0 2rem;
  position: sticky;
  top: 0;
  left: 0;
  background: var(--third-color);

  @media screen and (max-width: 1000px) {
    display: none;
  }
  .center {
    display: flex;
    align-items: center;
    justify-content: center;
  }
  h2 {
    height: 100%;
    display: flex;
    align-items: center;
    font-weight: 600;
    font-size: 14px;
    color: var(--fourth-color);
    margin: 0;
    padding: 0;
  }
`;
