//   const maxLever = info ? Math.max(...(info?.tokenFactor.map((x) => lever({borrowFactor: x.borrowFactor, collateralFactor: x.collateralFactor})))!) : 0;


//   const { tokens } = poolprops;

  const closeButton = (
    <Button
      onClick={async () => {
        const kit = await getConnectedKit();
        // kit is connected to a wallet
        try {
          setConfirmLoading(true);
          const bank = (new kit.web3.eth.Contract(
            BANK_ABI.abi as AbiItem[],
            getAddress(Bank[network.chainId])
            ) as unknown) as HomoraBank;
          const spell = (new kit.web3.eth.Contract(
            UNI_SPELL.abi as AbiItem[],
            getAddress(props.pool.spell),
          ) as unknown) as UniswapV2SpellV1;
          const bytes = spell.methods.removeLiquidityWStakingRewards(
            props.pool.tokens[0]!.address, 
            props.pool.tokens[1]!.address, 
            [
              MaxUint256.toString(),
              0, 
              MaxUint256.toString(), 
              MaxUint256.toString(), 
              0, 
              0, 
              0
            ],
            props.pool.wrapper,
          ).encodeABI()
          const tx = await bank.methods
            .execute(
                props.positionId,
                props.pool.spell,
                bytes,
            ).send({
              from: kit.defaultAccount,
              gasPrice: DEFAULT_GAS_PRICE,
              gas: 10000000,
            });
          toastTx(tx.transactionHash);
        } catch (e) {
          toast(e.message);
        } finally {
          setConfirmLoading(false);
        }
      }}
    >
      Close
    </Button>
  );

  const harvestButton = (<Button
  onClick={async () => {
    const kit = await getConnectedKit();
    // kit is connected to a wallet
    try {
      setConfirmLoading(true);
      const bank = (new kit.web3.eth.Contract(
        BANK_ABI.abi as AbiItem[],
        getAddress(Bank[network.chainId])
        ) as unknown) as HomoraBank;
      const spell = (new kit.web3.eth.Contract(
        UNI_SPELL.abi as AbiItem[],
        getAddress(props.pool.spell),
      ) as unknown) as UniswapV2SpellV1;
      console.log(props.pool.tokens[0]!.address, 
        props.pool.tokens[1]!.address, props.positionId);
      const bytes = spell.methods.removeLiquidityWStakingRewards(
        props.pool.tokens[0]!.address, 
        props.pool.tokens[1]!.address, 
        [
          MaxUint256.toString(),
          0, 
          MaxUint256.toString(), 
          MaxUint256.toString(), 
          0, 
          0, 
          0
        ],
        props.pool.wrapper,
      ).encodeABI()
      const tx = await bank.methods
        .execute(
            props.positionId,
            props.pool.spell,
            bytes,
        ).send({
          from: kit.defaultAccount,
          gasPrice: DEFAULT_GAS_PRICE,
          gas: 10000000,
        });
      toastTx(tx.transactionHash);
    } catch (e) {
      toast(e.message);
    } finally {
      setConfirmLoading(false);
    }
  }}
>
  Harvest
</Button>
);

  const urlext = props.positionId + "/" + props.collateralSize + "/" + props.pool.name + "/" + props.pool.wrapper + "/" + props.pool.spell + "/" + props.pool.lp + "/"
    + props.pool.tokens.map((tok) => tok.address)

  return (
    <Row>
      <td>
      <FarmInfo props={props.pool} />
      </td>
      <td>A lot</td>
      <td>A lot</td>
      <td>High</td>
      <td>High</td>
      <td
        css={css`
          text-align: right;
        `}
      >
          { confirmLoading ? <Spinner /> : (
        <Flex sx={{justifyContent: "center", alignItems: "center", flexDirection: "column", gap: "6px"}}>

          <Flex sx={{justifyContent: "center", alighnItems: "center", gap: "6px"}}>
            <Button onClick={() => {
             history.push(`positions/add/${urlext}`)}
            }>
              Add
            </Button>
            <Button onClick={() => {
             history.push(`positions/remove/${urlext}`)}
            }>
              Remove
            </Button>
          </Flex>
          <Flex sx={{justifyContent: "center", alighnItems: "center", gap: "6px"}}>
            {closeButton}
            {harvestButton}
          </Flex>
          </Flex>
          )}

      </td>
    </Row>
  );
};

const Row = styled.tr`
  height: 72px;
`;
