    //   <td>100%</td>
    //   <td>10%</td>
    //   <td>
    //     <Flex sx={{ flexDirection: "column", gap: "6px"}}>
    //       <span>Trading Fees: 10%</span>
    //       <span>Yield Farming: 0%</span>
    //     </Flex>
    //   </td>
    //   <td>
    //     <Flex sx={{ flexDirection: "column", alignItems: "center", gap: "6px"}}>
    //       {tokens.map((tok, index) => (
    //         <TokenBorrowInfo key={tok.address} token={tok} apy={info && info.borrowRate.length > 0 ? humanFriendlyWei(info.borrowRate[index]!): "--"} />
    //       ))}
    //     </Flex>
    //   </td>
    //   <td>{String(maxLever).concat("X")}</td>
    //   <td
    //     css={css`
    //       text-align: right;
    //     `}
    //   >
    //     <Button onClick={() => {
    //       setPool(poolInfo);
    //       setTab(false);
    //       setPage(Page.Supply);
    //     }}
    //       >
    //       Farm
    //     </Button>
    //   </td>
    // </Row>
    <Item key={poolInfo.name}>
        <div className='flexFirst'>
            <img src={poolInfo.tokens[0]!.logoURL} alt='alt' className='image' />
            <div className='block'>
            <h3>{poolInfo.name}</h3>
            <h2>{poolInfo.tokens[0]!.symbol}</h2>
            </div>
        </div>
        <div className='centerMiddle thin'>{"--"}</div>
        <div className='centerMiddle thin'>
            {"--"}
        </div>
        <div className='flex height'>
            <div className='block'>
            <h3>{"--"}</h3>
            <h3>{"--"}</h3>
            <h3>{"--"}</h3>
            </div>
            <div className='block left'>
            <h2>{"--"}</h2>
            <h2>{"--"}</h2>
            <h2>{"--"}</h2>
            </div>
        </div>
        <div className='flexColumn'>
            <div className='flex margin'>
            <img src={poolInfo.tokens[0]!.logoURL} alt='eth' />
            <h2>{"--"}</h2>
            </div>
            <div className='flex'>
            <img src={poolInfo.tokens[1]!.logoURL} alt='eth' />
            <h2>{"--"}</h2>
            </div>
        </div>
        <div className='centerMiddle large'>{String(maxLever).concat("X")}</div>
        <div className='end'>
            <Link to='/farm/farm'>FARM</Link>
        </div>
    </Item>
  );
};
