import React from "react";
import {
  Button,
  Card,
  Flex,
  Heading,
  Text,
  Spinner,
} from "theme-ui";
import { COLLATERAL_TOKENS, DEFAULT_GAS_PRICE, safeBoxMap } from "src/config";
import { BatteryMedium, CaretLeft } from "phosphor-react";
import { useContractKit } from "@celo-tools/use-contractkit";
import { useERC } from "src/hooks/useERC";
import { useERCmulti } from "src/hooks/useERCmulti";
import { TokenInputForm } from "src/components/TokenInputForm";
import {  RecoilRoot,  atom,  selector,  useSetRecoilState, useRecoilState, useRecoilValue } from 'recoil';
import { BlockText } from "src/components/BlockText";
import { Token } from "src/utils/token";
import { lpToken, Bank } from "src/config";
import { TokenAmountInfo } from "src/components/TokenAmountInfo";
import { fromWei, toWei, toBN, AbiItem } from "web3-utils";
import { toastTx } from "src/utils/toastTx";
import { toast } from "react-toastify";
import BANK_ABI from "src/abis/dahlia_contracts/HomoraBank.json";
import UNI_SPELL from "src/abis/dahlia_contracts/UniswapV2SpellV1.json";
import { HomoraBank } from "src/generated/HomoraBank";
import { UniswapV2SpellV1 } from "src/generated/UniswapV2SpellV1";
import { getAddress } from "ethers/lib/utils";
import { MaxUint256 } from "@ethersproject/constants";
import ERC20_ABI from "src/abis/fountain_of_youth/ERC20.json";
import { ERC20 } from "src/generated/ERC20";
import { humanFriendlyNumber } from "src/utils/number";
import { addFarmState, addPageState, addPage } from "src/pages/Position/Add/add";
import { poolState } from "src/pages/Farm/newFarm/NewFarm";


export const Confirm: React.FC = () => {
  const { getConnectedKit, network } = useContractKit();
  const [approveLoading, setApproveLoading] = React.useState(false);
  const [confirmLoading, setConfirmLoading] = React.useState(false);
  const [buttonLoading, setButtonLoading] = React.useState(true); 
  const [pool] = useRecoilState(poolState);
  const setPage = useSetRecoilState(addPageState)
  const [farm] = useRecoilState(addFarmState);
  const scale = toBN(2).pow(toBN(112)); 

  const lpTok: Token = new Token({
    ...lpToken,
    address: pool.lp,
  });

  const [tokenStates, refetchTokenStates] = useERCmulti(pool.tokens)
  const [erc, refetchERC] = useERC(lpTok.address, Bank[network.chainId]);

  const approveButton = (token: Token): any => {
    return (
    <Button
      onClick={async () => {
        const kit = await getConnectedKit();
        // kit is connected to a wallet
        try {
          setApproveLoading(true);
          const ERCToken = (new kit.web3.eth.Contract(
            ERC20_ABI as AbiItem[],
            token.address!,
          ) as unknown) as ERC20; 
          const tx = await ERCToken.methods
            .approve(Bank[network.chainId], MaxUint256.toString())
            .send({
              from: kit.defaultAccount,
              gasPrice: DEFAULT_GAS_PRICE,
            });
          toastTx(tx.transactionHash);
          refetchERC();
          refetchTokenStates();
        } catch (e) {
          toast(e.message);
        } finally {
          setApproveLoading(false);
        }
      }}
    >
      {"Approve ".concat(token.symbol)}
    </Button>
  )};

  const confirmButton = (
    <Button
      onClick={async () => {
        const kit = await getConnectedKit();
        // kit is connected to a wallet
        try {
          setConfirmLoading(true);
          const bank = (new kit.web3.eth.Contract(
            BANK_ABI.abi as AbiItem[],
            getAddress(Bank[network.chainId])
            ) as unknown) as HomoraBank;
          const spell = (new kit.web3.eth.Contract(
            UNI_SPELL.abi as AbiItem[],
            getAddress(pool.spell),
          ) as unknown) as UniswapV2SpellV1;
          const bytes = spell.methods.addLiquidityWERC20(
            pool.tokens[0]!.address, 
            pool.tokens[1]!.address, 
            [
              farm.tokenSupply![0]!.toString(),
              farm.tokenSupply![0]!.toString(), 
              farm.lpSupply!.toString(), 
              farm.tokenBorrow![0]!.toString(), 
              farm.tokenBorrow![1]!.toString(), 
              0, 
              0, 
              0
            ],
          ).encodeABI()
          const tx = await bank.methods
            .execute(
                farm.positionId!,
                pool.spell,
                bytes,
            ).send({
              from: kit.defaultAccount,
              gasPrice: DEFAULT_GAS_PRICE,
              gas: 10000000,
            });
          toastTx(tx.transactionHash);
          refetchERC();
        } catch (e) {
          toast(e.message);
        } finally {
          setConfirmLoading(false);
        }
      }}
    >
      Confirm
    </Button>
  );

  const loading = approveLoading || confirmLoading || buttonLoading;
  const button = React.useMemo(() => {
    let b: any[] = []
    if (tokenStates) {
      for (let i = 0; i < tokenStates.length; i += 1) {
        if (tokenStates[i] ){
          const amountBN = farm.tokenSupply![i]!
          if (amountBN.gt(tokenStates[i]?.allowance!)) {
            b.push(approveButton(pool.tokens![i]!));
            if (buttonLoading) setButtonLoading(false);
          }
        }
      }
      if (b.length === 0) {
        // eslint-disable-next-line react-hooks/exhaustive-deps
        b = [confirmButton]; 
        if (buttonLoading) setButtonLoading(false);
      }
    }
    if (erc) {
      const amountBN = farm.lpSupply!;
      if (amountBN.gt(erc.allowance)) {
        b.push(approveButton(lpTok))
      }
      if (buttonLoading) setButtonLoading(false)
    }
    return b; 
  }, [tokenStates, farm.tokenSupply, pool.tokens, erc, farm.lpSupply])

  if (!farm!) return null;

  return (
    <Flex sx={{ alignItems: "center", flexDirection: "column" }}>
      <Card sx={{ width: "100%", maxWidth: "800px" }} py={4} px={3}>
        <Flex
          onClick={() => {
            setPage(addPage.Borrow);
          }}
          sx={{ alignItems: "center", cursor: "pointer" }}
          mb={4}
        >
          <CaretLeft size={28} />
          <Text>Back</Text>
        </Flex>
        <Flex mb={4}>
          <Heading as="h2" mr={2}>
            Position Breakdown
          </Heading>
        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
          <BlockText variant="primary">My position has</BlockText>
          <Flex sx={{ justifyContent: "left", gap: "8px", alignItems: "center"}}>
            {farm.existingBalance && pool.tokens.map((tok, index) => 
            <Flex
            sx={{
              alignItems: "center",
              mr: 4,
              padding:2,
              borderStyle: "solid",
              borderRadius: "10px",
            }}
          >
              <TokenAmountInfo key={tok.address} token={tok} amount={fromWei(farm.existingBalance![index]!)} />       
            </Flex>
          )}
          </Flex>
        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
          <BlockText variant="primary">I'm Supplying</BlockText>
          <Flex sx={{ justifyContent: "left", gap: "8px", alignItems: "center"}}>
            {farm.tokenSupply && pool.tokens.map((tok, index) => 
            <Flex
            sx={{
              alignItems: "center",
              mr: 4,
              padding:2,
              borderStyle: "solid",
              borderRadius: "10px",
            }}
          >
              <TokenAmountInfo key={tok.address} token={tok} amount={fromWei(farm.tokenSupply![index]!)} />       
            </Flex>
          )}
            <Flex
            sx={{
              alignItems: "center",
              mr: 4,
              padding:2,
              borderStyle: "solid",
              borderRadius: "10px",
            }}
            >
              {farm.lpSupply && <TokenAmountInfo key={lpTok.address} token={lpTok} amount={fromWei(farm.lpSupply!)} /> }     
            </Flex>
          </Flex>
        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
          <BlockText variant="primary">I'm Borrowing</BlockText>
          <Flex sx={{ justifyContent: "left", gap: "8px", alignItems: "center"}}>
            {pool.tokens.map((tok, index) => 
            <Flex
            sx={{
              alignItems: "center",
              mr: 4,
              padding:2,
              borderStyle: "solid",
              borderRadius: "10px",
            }}
          >
              <TokenAmountInfo key={tok.address} token={tok} amount={fromWei(farm.tokenBorrow![index]!)} />       
            </Flex>
          )}
          </Flex>
        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
          <BlockText variant="primary">Position Statistics</BlockText>
          <Flex sx={{ justifyContent: "left", gap: "8px", flexDirection: "column"}}>
            <BlockText>{"Est. Debt Ratio: ".concat(humanFriendlyNumber(farm.debtRatio!)).concat("/100")}</BlockText>
              <BlockText>{"Leverage: ".concat(humanFriendlyNumber(farm.lever!)).concat("X")}</BlockText>
              {/* change this to usd value */}
              <BlockText>{"Supply Value: ".concat(" ").concat(humanFriendlyNumber(farm.supplyValue!))}</BlockText>
              <BlockText>{"Borrow Value: ".concat(" ").concat(humanFriendlyNumber(farm.borrowValue!))}</BlockText>
              <BlockText>{"Position Value: ".concat(" ").concat(humanFriendlyNumber(farm.supplyValue! + farm.borrowValue!))}</BlockText>
              <BlockText>Farming APY: </BlockText>
              <BlockText>Trading Fee APY: </BlockText>
              <BlockText>Borrow APY: </BlockText>
              <BlockText>Total APY: </BlockText>
              <BlockText>Slippage Tolerance: </BlockText>
              <BlockText>Price Impact: </BlockText>
          </Flex>
        </Flex>
        <Flex sx={{ justifyContent: "center", mt: 6 }}>
            {loading ? (
              <Spinner />
            ) : (
              <Flex sx={{ justifyContent: "center", gap: "6px"}} >
                {button[0]}
              </Flex>                
            )}
        </Flex>
      </Card>
    </Flex>
  );
};
