import { useContractKit } from "@celo-tools/use-contractkit";
import { Token } from "src/utils/token";
import { AbiItem, toBN } from "web3-utils";
import BANK_ABI from "src/abis/dahlia_contracts/HomoraBank.json";
import CERC20_ABI from "src/abis/fountain_of_youth/CErc20Immutable.json";
import JUMPRATE_ABI from "src/abis/fountain_of_youth/JumpRateModelV2.json"; 
import { HomoraBank } from "src/generated/HomoraBank";
import { CErc20Immutable } from "src/generated/CErc20Immutable";
import { JumpRateModelV2 } from "src/generated/JumpRateModelV2";
import { Bank, safeBoxMap } from "src/config";
import React from "react";
import { useAsyncState } from "src/hooks/useAsyncState";
import { getAddress } from "ethers/lib/utils";
import { humanFriendlyWei } from "src/utils/eth";
import { useSafeBox } from "src/hooks/useSafeBox"
import { Item } from './EarnStyles';
import { Link } from 'react-router-dom';
import styled from "@emotion/styled";
import { css } from "@emotion/react";
import { TokenInfo } from "src/components/TokenInfo";
import { Button, Flex } from "theme-ui";
import { useHistory } from "react-router-dom"


interface Props {
  token: Token;
}

export const EarnEntry: React.FC<Props> = ({ token }: Props) => {
  const { kit, network } = useContractKit();
  const [safeBox] = useSafeBox(safeBoxMap.get(token.address)!);
  const history = useHistory(); 

  const bank = React.useMemo(() => (new kit.web3.eth.Contract(
    BANK_ABI.abi as AbiItem[],
    getAddress(Bank[network.chainId])
  ) as unknown) as HomoraBank, [kit, network]); 

  const call = React.useCallback(async () => {
    try {
        const bankInfo =  await bank.methods.getBankInfo(token.address).call();
        const cToken = (new kit.web3.eth.Contract(
          CERC20_ABI as AbiItem[],
          bankInfo.cToken,
        ) as unknown) as CErc20Immutable;
        const totalSupply = await cToken.methods.totalSupply().call();
        const totalBorrows = await cToken.methods.totalBorrows().call();
        const totalReserves = await cToken.methods.totalReserves().call();
        const reserveFactorMantissa = await cToken.methods.reserveFactorMantissa().call();
        const interestRateModel = await cToken.methods.interestRateModel().call();
        const jumpRate = (new kit.web3.eth.Contract(
          JUMPRATE_ABI as AbiItem[],
          interestRateModel,
        ) as unknown) as JumpRateModelV2;
        const supplyRate = await jumpRate.methods.getSupplyRate(totalSupply, totalBorrows, totalReserves, reserveFactorMantissa).call();
        // TODO: is supply rate the right thing?
        const utilRate = await jumpRate.methods.utilizationRate(totalSupply, totalBorrows, totalReserves).call();
        return {
            totalSupply: toBN(totalSupply),
            totalBorrows: toBN(totalBorrows),
            totalReserves: toBN(totalReserves),
            utilizationRate: toBN(utilRate),
            projectedAPY: toBN(supplyRate),
        }
    } catch (error) {
        console.log(error)
    }

}, [bank, token.address, kit])

  const [info] = useAsyncState(null, call);

  // TODO fix values

  return (
  //   <Item key={token.address}>
  //   <div className='flexFirst'>
  //     <img src={token.logoURL} alt='eth' className='image' />
  //     <h2>{token.symbol}</h2>
  //   </div>
  //   <div className='centerMiddle thin'>{info ? humanFriendlyWei(info.projectedAPY).concat("%") : "--"}</div>
  //   <div className='centerMiddle thin'>{info ? humanFriendlyWei(info.totalSupply) : "--"}</div>
  //   <div className='centerMiddle thin'>{info ? humanFriendlyWei(info.totalBorrows) : "--"}</div>
  //   <div className='centerMiddle thin'>{info ? humanFriendlyWei(info.utilizationRate.mul(toBN(100))).concat("%") : "--"}</div>
  //   <div className='centerMiddle thin'>{safeBox ? humanFriendlyWei(safeBox.balance).concat(" d").concat(token.symbol) : "--"}</div>
  //   <div className='end'>
  //     <Link to={"earn/supply/".concat(token.address)}>Supply</Link>
  //     <Link to={"earn/withdraw/".concat(token.address)}>Withdraw</Link>
  //   </div>
  // </Item>
    <Row>
    <td>
      <TokenInfo token={token} />
    </td>
    <td>{info ? humanFriendlyWei(info.projectedAPY).concat("%") : "--"}</td>
    <td>{info ? humanFriendlyWei(info.totalSupply) : "--"}</td>
    <td>{info ? humanFriendlyWei(info.totalBorrows) : "--"}</td>
    <td>{info ? humanFriendlyWei(info.utilizationRate.mul(toBN(100))).concat("%") : "--"}</td>
    <td>{safeBox ? humanFriendlyWei(safeBox.balance).concat(" d").concat(token.symbol) : "--"}</td>
    <td
        css={css`
          text-align: right;
        `}
      >
        <div
          css={css`
            display: flex;
            gap: 8px;
            justify-content: flex-end;
          `}
        >
          <Flex sx={{gap: "6px"}}>
            <Button onClick={() => history.push(`earn/supply/${token.address}`)}>Supply</Button>
            <Button onClick={() => history.push(`earn/withdraw/${token.address}`)}>Withdraw</Button>
          </Flex>
        </div>
      </td>
  </Row>
  );
};

const Row = styled.tr`
  height: 72px;
`;
