import React from "react";
import { Flex } from "theme-ui";
import { BlockText } from "src/components/BlockText";
import { FarmView } from "src/pages/Farm/FarmTable"
import { PosTable } from "./PosTable";
import {  atom,  selector,  useRecoilState,  useRecoilValue,} from 'recoil';
import { Container } from "src/components/Container";
import { Token } from "src/utils/token";
import { Borrow } from "src/pages/Farm/Borrow";
import { Confirm } from "src/pages/Farm/Confirm";
import { Remove } from "src/pages/Position/Remove";

export enum posPage {
  Table,
  Remove, 
  RRepay, 
  RConfirm,
  Add,
}

export const posPageState = atom({
  key: 'posPageState',
  default: posPage.Table
})

export interface poolProps {
  name: string;
  werc20: string;
  wstaking: string | null;
  spell: string;
  lp: string;
  tokens: Token[];
}

const emptyPoolState : poolProps = {
  name: "",
  werc20: "",
  wstaking: null,
  spell: "",
  lp: "",
  tokens: [],
}

export const posPoolState = atom({
  key: 'posPoolState',
  default: emptyPoolState
})

export const Position: React.FC = () => {
  const [page] = useRecoilState(posPageState);

  return (
    <div>
      {
      page === posPage.Table ? (
          <Flex sx={{ flexDirection: "column"}}>
          <Flex
            sx={{
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              mt: ["33%", "10%"],
            }}
          >
            <BlockText variant="primary">
              Positions
            </BlockText>
            {/* TODO: format and make dynamic */}
            <BlockText>
              Manage your positions with ease.
            </BlockText>
          </Flex>
          <Container>
              <PosTable />
          </Container>
          </Flex>
        ) : (
          page === posPage.Remove ? (
            <Remove />
          ) : (
            page === posPage.RRepay ? (
            <Borrow />
          ) : (
            <Confirm />
          )))}
    </div>

  );
};
