import React from "react";
import {
  Button,
  Card,
  Flex,
  Heading,
  Text,
} from "theme-ui";
import { CaretLeft } from "phosphor-react";
import { useContractKit } from "@celo-tools/use-contractkit";
import { useERC } from "src/hooks/useERC";
import { useERCmulti } from "src/hooks/useERCmulti";
import { TokenInputForm } from "src/components/TokenInputForm";
import {  RecoilRoot,  atom,  selector,  useSetRecoilState, useRecoilState, useRecoilValue } from 'recoil';
import { pageState, Page, poolProps, poolState } from "src/pages/Farm";
import { showTabs } from "src/components/Header";
import { BlockText } from "src/components/BlockText";
import { Token } from "src/utils/token";
import { lpToken } from "src/config";
import { TokenAmountInfo } from "src/components/TokenAmountInfo";
import { farmState } from "src/pages/Farm/Supply";
import { fromWei, toWei, toBN, AbiItem } from "web3-utils";
import { TokenSlider } from "src/components/TokenSlider";
import BANK_ABI from "src/abis/dahlia_contracts/HomoraBank.json";
import PROXYORACLE_ABI from "src/abis/dahlia_contracts/ProxyOracle.json";
import COREORACLE_ABI from "src/abis/dahlia_contracts/CoreOracle.json";
import { HomoraBank } from "src/generated/HomoraBank";
import { ProxyOracle } from "src/generated/ProxyOracle";
import { CoreOracle } from "src/generated/CoreOracle";
import { Bank } from "src/config";
import { getAddress } from "ethers/lib/utils";
import { useAsyncState } from "src/hooks/useAsyncState";
import { humanFriendlyNumber } from "src/utils/number";


export const Borrow: React.FC = () => {
  const scale = toBN(2).pow(toBN(112)); 
  const [pool] = useRecoilState(poolState);
  const setPage = useSetRecoilState(pageState)
  const [farm, setFarm] = useRecoilState(farmState);
  const zeroAdd = "0x0000000000000000000000000000000000000000"


  const lpTok: Token = new Token({
    ...lpToken,
    address: pool.lp,
  });

  const [tokenStates, refetchTokenStates] = useERCmulti(pool.tokens)
  const [amounts, setAmounts] = React.useState(Array(pool.tokens.length).fill(".7"))
  const [erc, refetchERC] = useERC(lpTok.address);

  const { kit, network } = useContractKit();

  const bank = React.useMemo(() => (new kit.web3.eth.Contract(
    BANK_ABI.abi as AbiItem[],
    getAddress(Bank[network.chainId])
  ) as unknown) as HomoraBank, [kit, network]); 

  const call = React.useCallback(async () => {
    try {
        const factors = [];
        const prices = [];
        // const collatValue = [];
        const borrowValue = [];
        const oracle = await bank.methods.oracle().call();
        const proxyOracle = (new kit.web3.eth.Contract(
          PROXYORACLE_ABI.abi as AbiItem[],
          oracle,
        ) as unknown) as ProxyOracle;
        const source = await proxyOracle.methods.source().call();
        const coreOracle = (new kit.web3.eth.Contract(
          COREORACLE_ABI.abi as AbiItem[],
          source,
        ) as unknown) as CoreOracle;
        for (let i: number = 0; i < pool.tokens.length; i += 1) {
          const factor = await proxyOracle.methods.tokenFactors(pool?.tokens[i]!.address).call();
          factors.push(factor);
          const price = await coreOracle.methods.getCELOPx(pool?.tokens[i]!.address).call();
          prices.push(toBN(price));
          // const collat = await proxyOracle.methods.asETHCollateral(pool.werc20, pool?.tokens[i]!.address, toWei(amounts[i]).add(farm!.tokenSupply![i]!), "").call();
          // collatValue.push(collat);
          const borrow = await proxyOracle.methods.asETHBorrow(pool?.tokens[i]!.address, toWei(amounts[i]), zeroAdd).call();
          borrowValue.push(borrow);
        }
        return {
          tokenFactor: factors,
          celoPrices: prices,
          // collatValue,
          borrowValue,
        };
    } catch (error) {
        console.log(error)
    }

}, [bank.methods, kit.web3.eth.Contract, pool.tokens, pool.werc20, amounts, farm])


const [info] = useAsyncState(null, call);
if (info) {
  info.celoPrices.map((x) => console.log(Number(fromWei(x!)) / Number(fromWei(scale))))
}
const lever = () => {
  const borrowValue = info ? amounts.map((x, i) => x * (Number(fromWei(info?.celoPrices[i]!)) / Number(fromWei(scale)))).reduce((sum, current) => sum + current, 0) : 0; 
  const supplyValue = info ? farm.tokenSupply!.map((x, i) => Number(fromWei(x)) * (Number(fromWei(info?.celoPrices[i]!)) / Number(fromWei(scale)))).reduce((sum, current) => sum + current, 0) : 0; 
  return 1 + (borrowValue / supplyValue)
}

const debtRatio = () => {

}

console.log(amounts, farm.tokenSupply)

  const continueButton = (
    <Button
      onClick={() => {
        setFarm({
          ...farm,
          tokenBorrow: amounts.map((x) => toBN(toWei(String(x)))),
          });
        setPage(Page.Confirm); 
      }}
      >
        Continue
      </Button>
  )

  return (
    <Flex sx={{ alignItems: "center", flexDirection: "column" }}>
      <Card sx={{ width: "100%", maxWidth: "800px" }} py={4} px={3}>
        <Flex
          onClick={() => {
            setPage(Page.Supply);
          }}
          sx={{ alignItems: "center", cursor: "pointer" }}
          mb={4}
        >
          <CaretLeft size={28} />
          <Text>Back</Text>
        </Flex>
        <Flex mb={4}>
          <Heading as="h2" mr={2}>
            Farm
          </Heading>
        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
          <BlockText variant="primary">I'm Supplying</BlockText>
          <Flex sx={{ justifyContent: "left", gap: "8px", alignItems: "center"}}>
            {pool.tokens.map((tok, index) => 
            <Flex
            sx={{
              alignItems: "center",
              mr: 4,
              padding:2,
              borderStyle: "solid",
              borderRadius: "10px",
            }}
          >
              <TokenAmountInfo key={tok.address} token={tok} amount={fromWei(farm.tokenSupply![index]!)} />       
            </Flex>
          )}
            <Flex
            sx={{
              alignItems: "center",
              mr: 4,
              padding:2,
              borderStyle: "solid",
              borderRadius: "10px",
            }}
            >
              <TokenAmountInfo key={lpTok.address} token={lpTok} amount={fromWei(farm.lpSupply!)} />       
            </Flex>
          </Flex>
        </Flex>
        <Flex sx={{mb: 2, mt: "25px"}}>
          <BlockText variant="primary">Borrows</BlockText>
        </Flex>
        <BlockText mb={2}>Est. Debt Ratio: 65.79/100</BlockText>
        <BlockText mb={2}>{"Leverage: ".concat(humanFriendlyNumber(lever())).concat("X")}</BlockText>
          {pool.tokens.map((tok, index) => 
            <TokenSlider key={tok.address} token={tok} amount={amounts[index]}
            setAmount={(s: string) => setAmounts(amounts.map((x, i) => i === index ? s : x))} 
            max={"2.1"}
             />
          )}
        <Flex sx={{ justifyContent: "center", mt: 6 }}>
          {
          (amounts.every((x) => x === "0" || x === "")) ? (
            <Button disabled={true}>Must supply</Button>
          ) : (
            continueButton
          )}
        </Flex>
      </Card>
    </Flex>
  );
};
