import {
  Container, 
  Title, 
  Description,
  Wrapper,
  Background,
  CardContainer,
  InfoContainer,
  Header,
} from "src/components/MainPages";
import Image from 'src/images/background.png';
import { FARMS } from "src/config";
import { SimpleTable } from "src/components/SimpleTable";
import { css } from "@emotion/react";
import { FarmEntry } from "src/pages/Farm/FarmEntry";
import {  atom,  selector,  useRecoilState,  useRecoilValue,} from 'recoil';
import { Token } from "src/utils/token";

export enum Page {
  Table,
  Supply, 
  Borrow, 
  Confirm,
}

export const pageState = atom({
  key: 'pageState',
  default: Page.Table
})

export interface poolProps {
  name: string;
  wrapper: string;
  spell: string;
  lp: string;
  tokens: Token[];
}

const emptyPoolState : poolProps = {
  name: "",
  werc20: "",
  spell: "",
  lp: "",
  tokens: [],
}

export const poolState = atom({
  key: 'poolState',
  default: emptyPoolState
})

export const Farm = () => {
  return (
    <Wrapper>
      <Container>
        <Title>Farm</Title>
        <Description>
          Unlock leverage up to <span>2.5x</span>
        </Description>
      </Container>
      <CardContainer>
      <SimpleTable>
    <thead>
      <tr>
        <th
          css={css`
            text-align: left !important;
          `}
        >
          Pool
        </th>
        <th>Projected APY</th>
        <th>Pool APY</th>
        <th></th>
        <th>Borrow APY</th>
        <th>Max Leverage</th>
        <th />
      </tr>
    </thead>
    <tbody>
      {FARMS.map((farm) => (
        <FarmEntry key={farm.name} werc20={farm.werc20} name={farm.name} wstaking={farm.wstaking} spell={farm.spell} tokens={farm.tokens} lp={farm.lp} />
      ))}
    </tbody>
  </SimpleTable>
      </CardContainer>
      <Background src={Image} alt='Background Logo' />
    </Wrapper>
  );
};
