import React from "react";
import {
  Button,
  Card,
  Flex,
  Heading,
  Text,
} from "theme-ui";
import { CaretLeft } from "phosphor-react";

import { useERC } from "src/hooks/useERC";
import { useERCmulti } from "src/hooks/useERCmulti";
import { TokenInputForm } from "src/components/TokenInputForm";
import {  RecoilRoot,  atom,  selector,  useSetRecoilState, useRecoilState } from 'recoil';
import { pageState, Page, poolProps, poolState } from "src/pages/Farm";
import { showTabs } from "src/components/Header";
import { BlockText } from "src/components/BlockText";
import { Token } from "src/utils/token";
import { lpToken } from "src/config";


export const Borrow: React.FC = () => {
  const [pool] = useRecoilState(poolState);
  const setPage = useSetRecoilState(pageState)
  const setTab = useSetRecoilState(showTabs);

  const lpTok: Token = new Token({
    ...lpToken,
    address: pool.lp,
  });

  const [tokenStates, refetchTokenStates] = useERCmulti(pool.tokens)
  const [amounts, setAmounts] = React.useState(Array(pool.tokens.length).fill("0"))

  const [lpAmount, setLPAmount] = React.useState("0")
  const [erc, refetchERC] = useERC(lpTok.address);

  const continueButton = (
    <Button
      onClick={() => {
        setPage(Page.Confirm); 
      }}
      >
        Continue
      </Button>
  )

  return (
    <Flex sx={{ alignItems: "center", flexDirection: "column" }}>
      <Card sx={{ width: "100%", maxWidth: "800px" }} py={4} px={3}>
        <Flex
          onClick={() => {
            setPage(Page.Supply);
          }}
          sx={{ alignItems: "center", cursor: "pointer" }}
          mb={4}
        >
          <CaretLeft size={28} />
          <Text>Back</Text>
        </Flex>
        <Flex mb={4}>
          <Heading as="h2" mr={2}>
            Farm
          </Heading>

        </Flex>
        <Flex sx={{flexDirection: "column", gap: "25px", mb: 10}}>
        <BlockText variant="primary">I'm Supplying</BlockText>

          {pool.tokens.map((tok, index) => 
            <TokenInputForm key={tok.address} token={tok} amount={amounts[index]}
             setAmount={(s: string) => setAmounts(amounts.map((x, i) => i === index ? s : x))} 
             balance={tokenStates && tokenStates![index]! ? tokenStates![index]?.balance! : null}
            />
          )}
        </Flex>
        <Flex sx={{mt: "25px"}}>
          <BlockText variant="primary">Supply LP Token</BlockText>
        </Flex>
        <TokenInputForm token={lpTok} amount={lpAmount} setAmount={setLPAmount} balance={erc ? erc.balance : null}/>
        <Flex sx={{ justifyContent: "center", mt: 6 }}>
          {
          (amounts.every((x) => x === "0" || x === "") && (lpAmount === "0" || lpAmount === "")) ? (
            <Button disabled={true}>Must supply</Button>
          ) : (
            continueButton
          )}
        </Flex>
      </Card>
    </Flex>
  );
};
