import React from "react";
import { Box, Flex, useColorMode } from "theme-ui";
import { Logo } from "src/components/Logo";
import { ConnectWallet } from "src/components/ConnectWallet";
import { LockLaminatedOpen, Moon, Sun } from "phosphor-react";
import { NavLink } from "react-router-dom";
import styled from "@emotion/styled";
import { useLocation } from "react-router-dom";
import {  RecoilRoot,  atom,  selector,  useRecoilState,  useRecoilValue,} from 'recoil';

export const showTabs = atom({
  key: 'tabs',
  default: true,
})

export const Header: React.FC = () => {
  const location = useLocation();
  const [colorMode, setColorMode] = useColorMode();
  const [tab] = useRecoilState(showTabs);
  return (
    <Flex sx={{ justifyContent: "space-between", alignItems: "center" }} mb={4}>
      <Logo />
      <Flex sx={{ justifyContent: "center", margin: "auto" }}>
        {tab && (location.pathname === "/earn" || location.pathname === "/farm" || location.pathname === "/positions") && 
          <NavWrapper>
          <NavLink to="/earn">
            Earn
          </NavLink>
          <NavLink to="/farm">
            Farm
          </NavLink>
          <NavLink to="/position">
            Positions
          </NavLink>
          </NavWrapper>
        }
      </Flex>
      <Flex>
        <Flex
          sx={{
            alignItems: "center",
            backgroundColor: "secondaryBackground",
            mr: 4,
            px: 2,
            cursor: "pointer",
            borderRadius: "6px",
          }}
          onClick={() => {
            if (colorMode === "light") {
              setColorMode("dark");
            } else {
              setColorMode("light");
            }
          }}
        >
          {colorMode === "light" ? <Sun size={28} /> : <Moon size={28} />}
        </Flex>
        <ConnectWallet />
      </Flex>
    </Flex>
  );
};

const NavWrapper = styled.div`
  display: flex;
  gap: 36px;
  align-items: center;
  .active {
    color: #80ffdb;
  }
`;
