import React from "react";
import { Flex } from "theme-ui";
import { BlockText } from "src/components/BlockText";
import { FarmView } from "src/pages/Farm/FarmTable"
import {  RecoilRoot,  atom,  selector,  useRecoilState,  useRecoilValue,} from 'recoil';
import { Container } from "src/components/Container";

export enum Page {
  Table,
  Supply, 
  Borrow, 
  Comfirm,
}

export const pageState = atom({
  key: 'pageState',
  default: Page.Table
})

export interface farmProps {
  name: string;
  wstaking: string | null;
  spell: string;
  tokens: Token[];
}

const emptyPoolState : farmProps = {
  name: "",
  wstaking: null,
  spell: "",
  tokens: []
}

export const poolState = atom({
  key: 'poolState',
  default: emptyPoolState
})

export const Farm: React.FC = () => {
  const [page] = useRecoilState(pageState);

  return (
    <Flex sx={{ flexDirection: "column"}}>
    <Flex
      sx={{
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        mt: ["33%", "10%"],
      }}
    >
      <BlockText variant="primary">
        Farm
      </BlockText>
      {/* TODO: format and make dynamic */}
      <BlockText>
        Unlock leverage up to 9x.
      </BlockText>
    </Flex>
    <Container>
        <FarmView />
    </Container>
    </Flex>
  );
};
