import { css } from "@emotion/react";
import styled from "@emotion/styled";
import { useContractKit } from "@celo-tools/use-contractkit";
import { Button } from "theme-ui";
import { TokenInfo } from "src/components/TokenInfo";
import { Token } from "src/utils/token";
import { useEarnPool } from "src/components/useEarnPool";
import { useParams, useHistory } from "react-router-dom";
import { AbiItem, toBN, isAddress } from "web3-utils";
import BN from "bn.js";
import BANK_ABI from "src/abis/dahlia_contracts/HomoraBank.json"
import CERC20_ABI from "src/abis/fountain_of_youth/CErc20Immutable.json"
import { HomoraBank } from "src/generated/HomoraBank";
import { CErc20Immutable } from "src/generated/CErc20Immutable";
import { Bank } from "src/config";
import React from "react";
import { useAsyncState } from "src/hooks/useAsyncState";
import { getAddress } from "ethers/lib/utils";
import { ChainId } from "@celo-tools/use-contractkit";

interface Props {
  token: Token;
}

export const EarnPool: React.FC<Props> = ({ token }: Props) => {
  const { kit, network } = useContractKit();

  const bank = React.useMemo(() => (new kit.web3.eth.Contract(
    BANK_ABI.abi as AbiItem[],
    getAddress(Bank[network.chainId])
  ) as unknown) as HomoraBank, [kit, network]); 

const call = React.useCallback(async () => {
    try {
        const bankInfo =  await bank.methods.getBankInfo(token.address).call();
        const cToken = (new kit.web3.eth.Contract(
            CERC20_ABI as AbiItem[],
            bankInfo.cToken
        ) as unknown) as CErc20Immutable;
        const totalSupply = await cToken.methods.totalSupply().call();
        const totalBorrows = await cToken.methods.totalBorrows().call();
        const totalReserves = await cToken.methods.totalReserves().call();
        return {
            totalSupply: toBN(totalSupply),
            totalBorrows: toBN(totalBorrows),
            totalReserves: toBN(totalReserves),
            utilizationRate: toBN(0),
            projectedAPY: toBN(0),
        }
    } catch (error) {
        console.log(error)
    }

}, [bank, token.address])

  const [info] = useAsyncState(null, call);
  const history = useHistory();

  return (
    <Row>
      <td>
        <TokenInfo token={token} />
      </td>
      <td>{info ? info.projectedAPY.toString().concat("%") : "--"}</td>
      <td>{info ? info.totalSupply.toString() : "--"}</td>
      <td>{info ? info.totalBorrows.toString() : "--"}</td>
      <td>{info ? info.totalBorrows.toString() : "--"}</td>
      <td
        css={css`
          text-align: right;
        `}
      >
        <div
          css={css`
            display: flex;
            gap: 8px;
            justify-content: flex-end;
          `}
        >
          <Button onClick={() => history.push(`/supply/${token.address}`)}>Supply</Button>
          <Button onClick={() => history.push(`/withdraw/${token.address}`)}>Withdraw</Button>
        </div>
      </td>
    </Row>
  );
};

const Row = styled.tr`
  height: 72px;
`;
