//   const { tokens } = poolInfo;

  const closeButton = (
    <Button
      onClick={async () => {
        const kit = await getConnectedKit();
        // kit is connected to a wallet
        try {
          setConfirmLoading(true);
          const bank = (new kit.web3.eth.Contract(
            BANK_ABI.abi as AbiItem[],
            getAddress(Bank[network.chainId])
            ) as unknown) as HomoraBank;
          const spell = (new kit.web3.eth.Contract(
            UNI_SPELL.abi as AbiItem[],
            getAddress(info.pool.spell),
          ) as unknown) as UniswapV2SpellV1;
          const bytes = spell.methods.removeLiquidityWERC20(
            info.pool.tokens[0]!.address, 
            info.pool.tokens[1]!.address, 
            [
              MaxUint256.toString(),
              0, 
              MaxUint256.toString(), 
              MaxUint256.toString(), 
              0, 
              0, 
              0
            ],
          ).encodeABI()
          const tx = await bank.methods
            .execute(
                0,
                info.pool.spell,
                bytes,
            ).send({
              from: kit.defaultAccount,
              gasPrice: DEFAULT_GAS_PRICE,
              gas: 10000000,
            });
          toastTx(tx.transactionHash);
        } catch (e) {
          toast(e.message);
        } finally {
          setConfirmLoading(false);
        }
      }}
    >
